css:
@-moz-document domain(d4builds.gg) {
.builder {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
}

.builder__header { display: none !important }
.builder__content { height: 100% }
.builder__navigation { margin-top: 0 !important }
.skill__tree__header { padding: 10px !important; height: 40px }
.skill__tree { height: 100% }
.skill__tree__wrapper { height: 100% !important }

.paragon__master > .skill__tree__header { height: 100px }
.paragon__master { height: 100% }
.paragon__wrapper { height: 100% !important }

.app__footer, .app__header { display: none }
}
