css:
@-moz-document domain("borlangetidning.se") {
body, .site-nav, .site-nav__links, .site-nav__links-btn,#main-content,.page-broadcast .broadcast-article {
    background-color:#111 !important;
    color:white !important;
}
.teaser__title, .block-link-overlay, .timeline-list__item-link, .related-list__item {
    color:white !important;
}
.teaser__title a:visited {
    color: grey !important;
}
.teaser__standfirst, .txt-style-muted {
    color:gray !important
}
h1, h2, h3, h4, h5, h6, p, li, dt, dd, td, th, blockquote, button, input, textarea, select, option {
    color:white !important
}
a:hover {
    color : white !important;
}
[class^=plate-], [class*=" plate-"] {
    background-color: #444444;
    color: white !important;
}
.site-footer__secondary, .article__factbox, .chronology__content[data-title], .site-nav__more-menu, .site-header__sub-links, .search__form input {
    background-color: #222;
    color: white !important
}
header svg, .site-nav svg {
    background-color: rgba(250, 250, 250, 0.5);
    border: 2px solid rgba(0, 0, 0, 0.3);
}
}
