@-moz-document domain(vk.com) {
	/* Задний фон для постов */
	._post_content, 
	.PostActivityCaption, 
	.PostHeader, 
	.PostHeader--compact, 
	.PostHeader--inPost, 
	.js-PostHeader,
	.AvatarRichContainer, 
	.PostHeader__avatar,
	.post_content,
	.wall_post_text,
	.like_wrap, 
	._like_wall-213503156_7135,
	.MediaGrid__imageElement,
	.MediaGridContainerWeb--post,
	.feed_row,
	._like,
	._share,
	._add,
	.replies {
		background-color: #242425;
		border-radius: 10px;
		opacity: 1;
		z-index: 1;
	}

	/*.ProfileWrapper {
		opacity: 0.1;
	}*/

	.OwnerPageCover__in,
	.ProfileHeader__in,
	.ProfileHeader__ava,
	.ProfileHeader__wrapper,
	.ProfileInfo__fillInformationLink,
	.vkuiGroup, 
	.vkuiGroup--sizeX-regular, 
	.vkuiGroup--card, 
	.vkuiGroup--padding-m,
	.vkuiGroup__inner,
	#page_block_submit_post,
	#submit_post_box,
	#submit_post_error,
	.post_field_wrap, ._emoji_field_wrap,
	.page_block_h2, .ui_tabs, 
	.ui_tabs_new, 
	.ui_tabs_with_progress, 
	.ui_tabs_with_right_section,
	.ui_tabs_container {
		opacity: 1;
	}

	/* Стиль для прозрачного прямоугольника с круглыми углами */
	#side_bar_inner {
		position: relative;
		z-index: 1;
	}

	/*Боковой слайдер*/
	#side_bar_inner:before {
		content: "";
		position: absolute;
		top: -0.5;
		left: -6px;
		width: 100%;
		/*параметр height: настраивайте сами под себя, он опускает либо поднимает 
		боковой слайдер*/
		height: 340px;
		background-color: #242425;
		opacity: 1.0;
		border-radius: 10px; /* Радиус скругления углов */
		z-index: -1;
	}

	/* белый цвет текста */
	p,
	h1,
	h2,
	h3,
	h4,
	h5 {
		color: #ffffff;
	}

	/* Прозрачность на 30% */
	body {
		opacity: 0.9;
	}

	/* Установка фонового изображения */
	body {
		background-image: url("https://www.free-wallpapers.su/data/media/9158/big/gm9069.jpg");
		background-repeat: no-repeat;
		background-position: center center;
		background-attachment: fixed;
		background-size: cover;
	}
}
