css:
@-moz-document domain(vk.com) {
		/* Задний фон для постов */
		._post_content, 
		.PostActivityCaption, 
		.PostHeader, 
		.PostHeader--compact, 
		.PostHeader--inPost, 
		.js-PostHeader,
		.AvatarRichContainer, 
		.PostHeader__avatar,
		.post_content,
		.wall_post_text,
		.like_wrap, 
		._like_wall-213503156_7135,
		.MediaGridContainerWeb--post,
		.feed_row,
		._like,
		._share,
		._add,
		.replies {
			background-color: #242425;
			border-radius: 10px;
			opacity: 1;
			z-index: 1;
		}

		/* Стиль для прозрачного прямоугольника с круглыми углами */
		#side_bar_inner {
			position: relative;
			z-index: 1;
		}

		/* Боковой слайдер */
		.LeftMenuOld-module__container--G1UQ7:before {
			content: "";
			position: absolute;
			top: -0.5;
			left: -6px;
			width: 100%;
			height: 97%;
			background-color: #242425;
			opacity: 1.0;
			border-radius: 10px; /* Радиус скругления углов */
			z-index: -1;
		}

		/*Удаление рекламы*/
		.ads_ad_box6,
		.ads_ad_box2,
		.ver,
		.repeat_ver, 
		.size_site, 
		.redesign, 
		.first,
		#ads_left{
		  display: none !important;
		}

		/* Белый цвет текста */
		p,
		h1,
		h2,
		h3,
		h4,
		h5 {
			color: #ffffff;
		}


		/* Прозрачность */
		body{
			opacity: 0.9;
		}

		img,
		video{
		  opacity: 1;
		  
		}
		
		/*Фоновое изображение*/
		body {
                        background-image: url('https://www.free-wallpapers.su/data/media/9158/big/gm9069.jpg');
			background-repeat: no-repeat;
			background-position: center center;
			background-attachment: fixed;
			background-size: cover;
		}

}
