css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(https://habitica.com/) {
#app-header,
#new-stuff .center-block,
#new-stuff .mr-3,
.gem,
.gold,
.shop-sprite,
.reward,
.notifications,
.d-flex img {
    display: none !important;
}

.tasks-columns .reward {
    display: none !important;
}

.tasks-column.col-lg-3 {
    flex: 0 0 33% !important;
    max-width: 33% !important;
}

@media only screen and (max-width: 1000px) {
  .create-task-area {
    position: initial !important;
  }
  
  .tasks-columns .todo {
    max-height: 500px;
    overflow: hidden;
    overflow-y: scroll;
}
  
  .row .tasks-columns {
    flex-direction: column-reverse;
    width: 100%;
    margin-right: 0px;
    margin-left: 0px;
    gap: 10px;
  } 

  .tasks-column {
    min-height: 0px;
  }
  
  .tasks-column.col-lg-3 {
    max-width: 100% !important;
    flex: 1 !important;
  }
  .tasks-column[data-v-361a67a2] {
    min-height: 0px;
  }
  
  .tasks-navigation > .col-12 {
    width: auto !important;
    flex: auto;
  }
  .container-fluid {
    margin-bottom: 10px;
  }
  }
}
