css:
@-moz-document domain(youtube.com) {
	.html5-video-player{
	       border-radius: 20px;
	    }
	    #player-container{
 	      border-radius: 20px;
	    }
	    ytd-watch-metadata[modern-metapanel] #description.ytd-watch-metadata{
 	      background: rgba(55, 55, 55, 0.05);
	    }
	    .yt-spec-icon-badge-shape--type-notification .yt-spec-icon-badge-shape__badge {
 	      background-color: #0088ff;
	    }
	    html[dark], [dark] {
 	      --yt-spec-static-overlay-background-brand:rgba(0, 136, 255, 0.9);
        --yt-spec-button-chip-background-hover: rgba(60, 60, 60, 0.12);
        --yt-spec-mono-tonal-hover: rgba(0, 136, 255, 0.2);
        --ytd-searchbox-legacy-button-color: hsla(0, 3.9%, 40%, 0.08);
        --yt-spec-badge-chip-background: rgba(55, 55, 55, 0.05);
        --ytd-searchbox-legacy-border-color: hsl(0, 0%, 5.9%);
        --ytd-searchbox-legacy-button-border-color: hsl(0, 0%, 5.9%);
        --ytd-searchbox-legacy-button-color: hsla(0, 0%, 14.9%, 0.08);
        --ytd-searchbox-background: hsl(0, 0%, 6.7%);
	    }

	    .header.ytd-playlist-panel-renderer {
  	    background-color: rgba(19, 19, 19, 0.98);
	    }

	    ytd-playlist-panel-video-renderer[watch-color-update][can-reorder]:hover.dragging, ytd-playlist-panel-video-renderer[watch-color-update][can-reorder].dragging, ytd-playlist-panel-video-renderer[watch-color-update]:hover:not(.dragging) {
  	    background-color: rgba(0, 136, 255, 0.1);
	    }

	    .playlist-items.ytd-playlist-panel-renderer {
  	    background-color: rgba(19, 19, 19, 0.98);
   }
}
