/* ==UserStyle==
@name           CustomStyle CybHack x Resox
@namespace      cybhack.net/
@version        1.0.0
@description    CustomStyle CybHack x Resox
@author         Resox
==/UserStyle== */

@-moz-document domain(cybhack.net),
domain("cybhack.net") {
  body {
    background-image: url("/*[[cybhack-background]]*/");
	background-size: cover;
	background-attachment: fixed;
        background-repeat: no-repeat;
        color: rgb(214, 214, 214);
        font: 13 px /1.231 arial, helvetica, clean, sans-serif;
        *font-size: small;
        *font: x-small;
        width: 100%;
        height: 100%;
}
  
.button.button--cta, button.button a.button.button--cta {
    background: /*[[bgC]]*/;
    border-color: /*[[bgC]]*/;
    border-radius: 9px 9px 9px 9px;
    box-shadow: 0px 0px 20px 0px  /*[[bgC]]*/;
}

::-webkit-scrollbar-thumb {
    background: /*[[bgC]]*/;
}
  .button.button--cta:hover, button.button a.button.button--cta:hover, .button.button--cta:focus, button.button a.button.button--cta:focus {
    background: /*[[bgC]]*/;
    border-radius: 9px 9px 9px 9px;
}
.block-container {
opacity: 0.95;
border-radius: 9px 9px 9px 9px;
}
.button, button.button a.button {
    color: #ffffff;
    background: /*[[bgC]]*/;
    border-color: /*[[bgC]]*/;
    box-shadow: 0px 0px 20px 0px  /*[[bgC]]*/;
    border-radius: 9px 9px 9px 9px;
}
.button:hover, button.button a.button:hover, .button:focus, button.button a.button:focus {
    background: /*[[bgC]]*/;
    box-shadow: 0px 0px 20px 0px  /*[[bgC]]*/;
    border-radius: 9px 9px 9px 9px;
}
.button.button--primary, button.button a.button.button--primary {
    color: #ffff;
    background: /*[[bgC]]*/;
    border: none;
    border-color: /*[[bgC]]*/;
}
.button.button--primary:hover, button.button a.button.button--primary:hover, .button.button--primary:focus, button.button a.button.button--primary:focus {
    color: #ffff;
    background: /*[[bgC]]*/;
}
a:hover {
    color: #ffffff;
}
.p-staffBar {
    font-size: 1.4rem;
    color: rgba(255,255,255,0.8);
    background: #505050;
  background-color: /*[[bgC]]*/;
}
.label.label--contest {
    box-shadow: 0px 0px 20px 0px rgba(124, 77, 174, 1);
}
.label.label--important {
    box-shadow: 0px 0px 20px 0px rgba(136, 68, 68, 1);
}
.label.label--green, .userBanner.userBanner--green {
    box-shadow: 0px 0px 20px 0px rgba(29, 83, 59, 1);
}
.block[data-widget-key="forum_overview_forum_statistics"] .stats-progress .progress {
    background: /*[[bgC]]*/;
    box-shadow: 0px 0px 20px 0px  /*[[bgC]]*/;
}
.p-nav-opposite {
    border-left: 1px solid /*[[bgC]]*/;
}
.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus {
    background: /*[[bgC]]*/;
}
.button.button--link, button.button a.button.button--link {
    background: /*[[bgC]]*/;
    border: 1px solid /*[[bgC]]*/;
}
.blockLink.is-selected {
    color: /*[[bgC]]*/;
    border-left: 2px solid /*[[bgC]]*/;
}
.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus {
    color: #ffffff;
}
.block-tabHeader .tabs-tab.is-active {
    background: none;
    color: /*[[bgC]]*/;
    border-color: /*[[bgC]]*/;
}
.block-tabHeader .tabs-tab:hover {
    color: /*[[bgC]]*/;
    background: rgb(180 65 65 / 0%);
}
.button.button--link:hover, button.button a.button.button--link:hover, .button.button--link:focus, button.button a.button.button--link:focus {
    background: /*[[bgC]]*/;
}
.bbCodeBlock {
    border-left: 2px solid /*[[bgC]]*/;
}
.breadcrumb.block {
    border-radius: 9px 9px 9px 9px;
}
.p-pageWrapper .p-body-header {
    background: #292f3c;
}
element.style {
    color: #ffffff;
}
.u-dt[title] {
    color: rgb(255 255 255);
}
.fa-clock:before {
    color: rgb(255 255 255);
}
.listInline>li {
    display: inline;
    margin: 0;
    padding: 0;
    color: white;
}
.contest-block .c-main-block .c-title {
    color: /*[[bgC]]*/;
}
.contest-block .c-main-block .cm-block.block-counter .counter-background {
    background: /*[[bgC]]*/;
}
.has-reaction.reaction.reaction--imageHidden.reaction--1 i {
    color: /*[[bgC]]*/;

}
.has-reaction.reaction.reaction--imageHidden.reaction--1 i {
    color: /*[[bgC]]*/;
    text-shadow: 0px 0px 20px /*[[bgC]]*/;
}
.message a.link, .mess-stack a.link, .bbWrapper a.link {
    color: /*[[bgC]]*/;
}
.block-outer {
    padding: 8px 0px;
    background: rgb(255 255 255 / 0%);
}
.buttons-bottom-avatar .button:not(.button--dots) {
    background: /*[[bgC]]*/;
}
.buttons-bottom-avatar .button--dots {
    background: /*[[bgC]]*/;
}
.tc-fb-badge.tc-fb-userGroupBadge--staff {
    box-shadow: 0px 0px 20px 0px rgba(150, 68, 72, 1);
}
.pageNav-page.pageNav-page--current {
    color: #ffffff;
    box-shadow: 0 -2px /*[[bgC]]*/ inset;
}
.message-avatar-wrapper:before {
    border-left: 1px solid /*[[bgC]]*/;
    border-right: 1px solid /*[[bgC]]*/;
}
.input:focus, .input.is-focused, .inputGroup:focus-within, #XF .fr-box.fr-basic.is-focused, .codeEditor.CodeMirror.CodeMirror-focused {
    --input-border-heavy: /*[[bgC]]*/;
    --input-border-light: /*[[bgC]]*/;
}
.button.button--scroll, button.button a.button.button--scroll {
    background: /*[[bgC]]*/;
    box-shadow: 0px 0px 20px 0px /*[[bgC]]*/;
}
.button.button--scroll:hover, button.button a.button.button--scroll:hover, .button.button--scroll:focus, button.button a.button.button--scroll:focus {
    background: /*[[bgC]]*/;
}
*::selection {
    background: /*[[bgC]]*/;
}
.button.button--provider--steam, a.button.button--provider--steam {
    color: #ffffff;
    background-color: /*[[bgC]]*/;
    border-color: /*[[bgC]]*/;
}
.button.button--provider--tc_discord, a.button.button--provider--tc_discord {
    color: #fff;
    background-color: /*[[bgC]]*/;
    border-color: /*[[bgC]]*/;
}
.button.button--provider--tc_discord:hover, a.button.button--provider--tc_discord:hover, .button.button--provider--tc_discord:active, a.button.button--provider--tc_discord:active, .button.button--provider--tc_discord:focus, a.button.button--provider--tc_discord:focus {
    background-color: /*[[bgC]]*/;
}
.button.button--provider--steam:hover, a.button.button--provider--steam:hover, .button.button--provider--steam:active, a.button.button--provider--steam:active, .button.button--provider--steam:focus, a.button.button--provider--steam:focus {
    background-color: /*[[bgC]]*/;
}
.p-navgroup.p-navgroup--guest .button {
    color: #ffffff;
}
.notice.notice--primary {
    color: #dfdfdf;
    background: #363d4aeb;
    border: 2px solid /*[[bgC]]*/;
}
.notice.notice--primary .uix_noticeIcon {
    background: /*[[bgC]]*/;
}
.lfs ::-webkit-scrollbar-thumb:hover, .real-time-chat ::-webkit-scrollbar-thumb:hover, .menu-content ::-webkit-scrollbar-thumb:hover {
    background: /*[[bgC]]*/;
}
.lfs ::-webkit-scrollbar-thumb, .real-time-chat ::-webkit-scrollbar-thumb, .menu-content ::-webkit-scrollbar-thumb {
    background: /*[[bgC]]*/;
}
}
