css:
@-moz-document domain("chess.com") {
  /* Change the board colors */
  .board-tile-light, 
  .board-tile-dark {
    background-color: #f0d9b5 !important; /* Change to Chess24.com default square color */
  }

  /* Change the piece colors */
  .piece-merida-3d {
    fill: #f0d9b5 !important; /* Change to Chess24.com default piece color */
  }

  /* Change the border colors */
  .board-tile-border {
    stroke: #b58863 !important; /* Change to Chess24.com default border color */
  }

  /* Change the highlight colors */
  .board-tile-legal-move, 
  .board-tile-legal-capture {
    background-color: #c3a882 !important; /* Change to Chess24.com default highlight color */
  }

  /* Change the promotion square color */
  .promotion-highlight {
    background-color: #d4bca5 !important; /* Change to Chess24.com default promotion square color */
  }

  /* Change the notation colors */
  .notation-move, 
  .notation-capture {
    color: #f0d9b5 !important; /* Change to Chess24.com default notation color */
  }

  /* Hide the Chess.com watermark (optional) */
  .watermark {
    display: none !important;
  }
}
