css:
img[src="../media/images/avatar2.png"] {
  border-radius:50%;
}

img[id="menucontent_simgnav"] {
  display:none;
}

span[onclick="w3_close()"] {
  position: relative;
  z-index: 1;
  transition: transform 0.3s ease-in-out, background-color 0.3s ease-in-out;
  border-radius: 100px;
  margin-bottom: 5px;
  margin-left: 5px;
  margin-right: 5px !important;
  margin-top: 5px;
	width: 30% !important;
background-color: rgba(255,255,255,.4) !important;
}

span[onclick="w3_close()"]:hover {
  transform: scale(1.1);
  z-index: 2;
  background-color: #1abc9c !important;
  transition: transform 0.3s ease-in-out, background-color 0.3s ease-in-out;
}

div[id="mySidebar"] {
  background-color: rgba(0,0,0,.2) !important;
  backdrop-filter: blur(10px);
  padding-right: 20px;
  padding-left: 10px;
}

a[href="#"] {
  display: none !important;
}


ul[style="margin: 0; list-style-type: none"] {
  padding-left: 0px !important;
}

a[href$=".aspx"],div[data-target="#loginmodal"] {
  color: white !important;
  position: relative;
  background-color: rgba(0,0,0,.2) !important;
  z-index: 1;
  transition: transform 0.3s ease-in-out, background-color 0.3s ease-in-out;
  border-radius: 100px;
  margin-bottom: 5px;
  margin-left: 5px;
  margin-right: 5px;
  margin-top: 5px;
	width: 100% !important;
  
}

a[href$=".aspx"]:hover, div[data-target="#loginmodal"]:hover {
  transform: scale(1.1);
  z-index: 2;
  background-color: #1abc9c !important;
  transition: transform 0.3s ease-in-out, background-color 0.3s ease-in-out;
  color: black !important;
}

div[class="w3-card w3-round-large w3-padding-large w3-light-gray modal-body"] {
  background-color: rgba(0,0,0,.2) !important;
  transition-duration: 1s;
  vertical-align: center !important;
  backdrop-filter: blur(2px);
}

div[class="modal-content"] {
  background-color: rgba(0,0,0,0) !important;
  transition-duration: 1s;
  vertical-align: center !important;
  border: none !important;
  margin-top: 50%;
}

div[class="w3-card w3-round-large w3-padding-large w3-light-gray modal-body"]:hover {
  background-color: rgba(0,0,0,.2) !important;
  backdrop-filter: blur(7.5px);
  border-radius: 20px;
}

input[type="text"], input[type="password"] {
  border-radius: 200px !important;
  background-color: rgba(255,255,255, .3) !important;
	border: none !important;
  border: 1px solid rgb(0,0,0) !important;
  text-align: center;
}

input[name="ctl00$bodycontent$fname"], input[name="ctl00$bodycontent$pgmlan"] {
  border: 1px solid rgb(0,0,0) !important;
  text-align: center !important;
}

input[name="ctl00$bodycontent$fname"]:focus, input[name="ctl00$bodycontent$pgmlan"]:focus {
	 background-color: white !important;
}

input[type="text"]:focus, input[type="password"]:focus {
  background-color: rgba(0,0,0, .3) !important;
	border: none !important;
  color: white !important;
}


img[src="media/images/login.png"]{
  filter: hue-rotate(135deg);
}

div[class="w3-col w3-text-dark-gray"] {
  display: none !important;
}

input[type="submit"] {
  border-radius: 20px !important;
  background-color: rgba(255,255,255,.4) !important;
  transition-duration: .3s;
  margin-top: 15px;
  border: 1px solid #1abc9c;
}

input[type="submit"]:hover {
  transform: scale(1.1);
  z-index: 2;
  background-color: #1abc9c !important;
  transition-duration: .3s;
}

div[class="w3-card w3-round-large w3-padding-large w3-display-middle w3-light-gray"] {
  background-color: rgba(0,0,0,.5) !important;
}

div[class="w3-panel my-color1 w3-round-large w3-center "] {
  border-radius: 200px;
  background-color: rgba(255,255,255,.3) !important;
  transition-duration: .3s;
}

div[class="w3-panel my-color1 w3-round-large w3-center "]:hover {
  border-radius: 200px;
  background-color: #1abc9c !important;
  transition-duration: .3s;
}

div[class="w3-left-align w3-bar w3-top my-color1"] {
  background-color: #1abc9c !important;
}

div[onclick="w3_open()"] {
  position: relative;
  z-index: 1;
  transition: transform 0.3s ease-in-out, background-color 0.3s ease-in-out;
  border-radius: 100px;
  margin-bottom: 5px;
  margin-left: 5px;
  margin-right: 5px !important;
  margin-top: 5px;
background-color: rgba(255,255,255,.4) !important;
  
}

div[onclick="w3_open()"]:hover {
  transform: scale(.8);
  z-index: 2;
  background-color: #107863 !important;
  transition: transform 0.3s ease-in-out, background-color 0.3s ease-in-out;
}

div[class="w3-card w3-round-large w3-padding-large w3-display-middle w3-animate-opacity w3-light-gray"] {
  background-color: rgba(26,188,156,.6) !important;
  backdrop-filter: blur(10px);
}

img[src="../media/images/exam.png"] {
  display: none !important;
}

select[id="bodycontent_selsub"], select[id="bodycontent_selexp"] {
  border-radius: 200px;
}

a[href="studentlabselect.aspx"][class="w3-button w3-block w3-dark-grey"] {
	width: 50% !important;
	margin: auto;
  margin-bottom: 3%;
}
