css:
@-moz-document url(https://torrentgalaxy.to/),url(https://tgx.rs/) {
div.txdark a {
    color: white;
    font-weight: 100 !important;
    font-family: sans-serif;
    letter-spacing: 0.03em;
}
.tgxtablehead .tgxtablecell:nth-child(5),
.tgxtablerow .tgxtablecell:nth-child(5),
.tgxtablehead .tgxtablecell:nth-child(4),
.tgxtablerow .tgxtablecell:nth-child(4),
.tgxtablehead .tgxtablecell:nth-child(7),
.tgxtablerow .tgxtablecell:nth-child(7),
.tgxtablehead .tgxtablecell:nth-child(8),
.tgxtablerow .tgxtablecell:nth-child(8){
    display: none !important;
}

#main .col-lg-10.col-sm-12 .panel:nth-child(6),
#panelmain #intblock:nth-child(9)
{
    display:none;
}

a .fa-film{
  opacity:0;
  margin-left:-1.5em;
}

.tgxtablerow:hover {
    -ms-transform: none !important;
    -webkit-transform: none !important;
    transform: none !important;
    background-color: #121212 !important;
}

.wrapper {
    animation-duration: 500s;
}


div #panelheader.panel-heading.txdark {
    background-color: #080808;
    border-color: #4a4a4a;
}
div #intblockheader.panel-heading.txdark,#innerhead.tgxtablehead.int.txdark {
  background-color: #1c1c1c;
}

div #panelmain.outer.panel-primary.txdark,div #intblock.panel-primary.txdark,.fpboxheader.txdark {
    background-color: #222222;
    border-color: #222222;
}

.tgxtablerow:nth-of-type(even).txdark {
    background-color: #222222;
}
.tgxtablerow:nth-of-type(odd).txdark {
    background-color: #303030;
}
#intblockheader > h3.panel-title * {
  color:#626262 !important;
}

nav.txdark {
    background: none;
}
a[href*="stream"] span{
      color: #af6767!important;
    text-shadow: none !important;
  animation: none !important;
}



  
}
