css:
@-moz-document domain("ext.to") {
body, .status-block.setStatusButtons button, .footer-white, .year-date, .table-striped tbody tr:nth-of-type(2n+1), .tags-block a.badge.badge-primary, .keywords-info ul a.badge.badge-primary, .seasons-info .panel-heading.is-active, .short-seasons-block.movieSeasons, .collection-block.collectionWidget .head, .collection-block .collectionsWindow.withFavourites .favourites, .collection-block.collectionWidget.withFavourites .favourites, .seasons-block ul a.badge.badge-primary, .block-plot {
  background: #11131a;
}

.card {
  background: #141D26;
}

.card, .filmList .item small, .filmList .item ul.rating li, .jpaginator .count, .filmList__watch-stat, .filmList__watch-stat-runtime, .filmList .more, .checkbox__content .filter-name, .switcher__content .filter-name, .radio__content .filter-name, .jpaginator .pages li a, .title, .title a, .card-title, .card-title a, .info-title, .info-title a, .footer-brand, .footer-brand a, .footer-big h5, .footer-big h5 a, .footer-big h4, .footer-big h4 a, .media .media-heading, .media .media-heading a, .status-block.setStatusButtons button font, .status-block.setStatusButtons button:hover font, .status-block.setStatusButtons button.active font, .seasons-info .episode-item-title, .seasons-info .episode-item-stat, .seasons-info .panel-title, .tags-block a.badge.badge-primary, .keywords-info ul a.badge.badge-primary, .episodes-info .panel-title, .progress-info .episode-stat, .short-seasons-block .movieSeasons__episodes, .collection-block .collectionsWindow .head, .collection-block.collectionWidget .head, .ratingsBlock li a, .ratingsBlock .count, .seasons-block ul a.badge.badge-primary, .btn-search, .titleH4, .titleH4 a, .main-block table a, #torrent_files {
    color: #8798A5;
}
.card-title-header-block, .filmList__extra-info, .filmList__extra-info a, .filmList .item p, h1 a, .year-date, .ratingsBlock li a.noLink, .nav-tabs .nav-item .nav-link {
    color: #8798A5!important;
}

.collectionListContainer .collectionWidget .head span, .filmListContainer .collectionWidget .head span, .statusPageView .collectionWidget .head span, .statusWidget.done span, .statusWidget.future span, .statusWidget.never span, .statusWidget.no_status span, .statusWidget.now span, .iohideViewed_type-button .iohideViewed__switcher::before, .iohideFeature .iohideFeature__switcher::before, .start-block .share a, img[src="/static/img/no-poster2.png"], .status-block.setStatusButtons button img, .collection-block .collectionAddPopup .icon, .collection-block .collectionsWindow .icon, .collection-block.collectionWidget .icon {
    filter:invert(90)
}

.filmList__movie-name {
    color: #A9894F;
}

.nav-tabs .nav-link.active, .scroll-menu__list .button, .nav-tabs .nav-item.show .nav-link {
	border: 1px solid #11131a!important;
	background-color: #11131a!important;
	color: #8798A5;
}

.input__field, .selectBox .ui-selectmenu-button,  .ui-widget.ui-widget-content, .jpaginator .pages li, .nav-tabs .nav-item .nav-link, .bd-callout {
	border: 1px solid #11131a!important;
	background-color: #243447;
	color: #8798A5;
}

.ui-selectmenu-menu .ui-widget.ui-widget-content, .block-plot-tv {
	background-color: #243447;
}

.seasons-info button.checkEpisode, .tags-block a.badge.badge-primary, .keywords-info ul a.badge.badge-primary, .seasons-info .panel-group .panel, .seasons-block ul a.badge.badge-primary, .btn-search {
	border: 1px solid #243447;
}

.seasons-info .episode-item .episode-item-stats, .statusList.statusPageView .buttonPane, .panel .panel-heading, .nav-tabs, .file-name-line-td, .file-size-line-td {
	border-bottom: 1px solid #243447;
}

.file-line {
	border-top: 1px solid #243447;
}

.file-line-td, .collection-block .collectionsWindow.withFavourites .favourites, .collection-block.collectionWidget.withFavourites .favourites {
	border-left: 1px solid #243447;
}
}
