css:
@-moz-document domain('openlearninglibrary.mit.edu'){
/* Background */
body, header, footer, section, div, button, input, ul, a:hover, .course-wrapper, .MathJax{
    background-color: #1a1a1a !important;
}

/* Multiple Choice Outline */
label, input, .MathJax{
   border-color: #9c9c9c !important;
}

/* Text */
p, span, text, li, dt, dd, div{
    color: #9c9c9c !important;
}

/* Titles and Links */
h1, h2, h3, h4, h5, h6, a, input, .icon, .sequence-tooltip, .submit-label, .sequence-nav-button-label{
    color: #ebebeb !important;
}

/* Bookmark Red */
.course-tool-link, .course-tool-link .icon, .bookmark-text, .bookmark-icon, .username{
    color: #a31f34 !important;
}

/* Images */
img {
  filter: invert(1) !important;
}

/* Course Images */



.course-image{
  filter: none !important;
}
}
