css:
body {
    --custom-accent-hue: 350;
    --color-calendar-graph-day-L1-bg: hsl(var(--custom-accent-hue), 61%, 17%);
    --color-calendar-graph-day-L2-bg: hsl(var(--custom-accent-hue), 100%, 21%);
    --color-calendar-graph-day-L3-bg: hsl(var(--custom-accent-hue), 63%, 42%);
    --color-calendar-graph-day-L4-bg: hsl(var(--custom-accent-hue), 77%, 59%);
    --color-btn-primary-bg: hsl(var(--custom-accent-hue), 59%, 33%);
    --color-btn-primary-hover-bg: hsl(var(--custom-accent-hue), 55%, 40%);
    --color-btn-primary-selected-bg: hsl(var(--custom-accent-hue), 59%, 33%);
    --color-btn-primary-disabled-bg: hsla(var(--custom-accent-hue), 59%, 33%, 0.6);
    --color-accent-fg: hsl(var(--custom-accent-hue), 93%, 58%);
    --color-accent-emphasis: hsl(var(--custom-accent-hue), 84%, 52%);
    --color-accent-muted: hsla(var(--custom-accent-hue), 98%, 61%, 0.4);
    --color-accent-subtle: hsla(var(--custom-accent-hue), 98%, 61%, 0.1);
    --color-open-fg: hsl(var(--custom-accent-hue), 49%, 49%);
    --color-open-emphasis: hsl(calc(var(--custom-accent-hue) + 2), 59%, 33%);
    --color-success-fg: hsl(var(--custom-accent-hue), 49%, 49%);
    --color-success-emphasis: hsl(calc(var(--custom-accent-hue) + 2), 59%, 33%);
}
