/* ==UserStyle==
@name         Dark dutch Government & Belastingdienst
@namespace    USO Archive
@version      20230607.11.14
@description  Dark style for the dutch Government & Belastingdienst websites.
@author       Idomo
@license      NO-REDISTRIBUTION==/UserStyle== */
@-moz-document domain("belastingdienst.nl"), domain("government.nl") {
:root {
  --bg-color: #000;
  --fg-color: #fff;
  --light-blue: #242a2d;
  --light-orange: #272221;
  --light-yellow: #44433a;
  --light-gray: #444;
  --dark-gray: #222;
  --link-blue: #029eeb;
}

body {
  background-color: #000;
  color: #fff;
}

.header .navbar .breadcrumb,
.bldc-footer,
.site-footer .languages,
.form-check--boxed span.labeltext,
.bld-layer-featured .card,
section.bld-news-items,
.bld-kader-licht {
  background-color: var(--light-blue);
}

.bld-theme-tsl .bld-layer-featured .card,
.bld-toeslagen .bld-layer-featured .card,
.bld-theme-tsl .header .navbar .breadcrumb,
.bld-toeslagen .header .navbar .breadcrumb,
.bld-theme-tsl .bld-news-items,
.bld-toeslagen .bld-news-items,
.bld-theme-tsl .bldc-footer,
.bld-toeslagen .bldc-footer,
.bld-theme-tsl section.bld-layer-iah .bld-form,
.bld-toeslagen section.bld-layer-iah .bld-form{
  background-color: var(--light-orange);
}
  
.bld-iah-main .bld-letop, 
.bld-iah-main .message_letop, .bld-iah-main .message_letop,
.content_main .bld-letop,
.content_main .message_letop, 
.content_main .message_letop{
  background-color: var(--light-yellow);
}

.card,
.bld-subnavigatie #bld-subnavigatie-container > #to-menu,
.bld-subnavigatie #bld-subnavigatie-container > ul li.open > a{
  background-color: var(--dark-gray);
}

.bld-layer-contentoverview,
.feedbackBar,
.editorial-navigation.grey,
.bld-date,
.bld-layer-theme{
  background-color: var(--light-gray);
}

img#bld-img-logo,
.logo__figure{
  filter: drop-shadow(0 0.5px 2px #fff);
  background-color: rgba(255,255,255,0.08);
  padding: 0 0.5em;
}

.logo .logo__text,
.bld-subnavigatie ul > li > a,
.brick p:not(.copyright), .brick ul,
.editorial-navigation.grey .navItem a, .editorial-navigation.grey .navItem a:hover, .editorial-navigation.grey .navItem a:visited, .editorial-navigation.grey .navItem a p,
.table,
.bld-link-buttons li a, .bld-link-buttons>ul li a,
.card.bld-theme-card a{
  color: var(--fg-color);
}

a,
ul.bld-kliklijst > li > h2 > a:first-of-type,
ol.bld-stappenplan > li > h2 > a:first-of-type,
ul li::marker, ol li::marker,
.btn.minimal,
.editorial-navigation.grey .navItem h3, .editorial-navigation.grey .navItem span,
li.languages__list-item{
  color: var(--link-blue);
}

.dropdown-menu {
  filter: invert(1);
}
}
