css:
@-moz-document url-prefix("https://v2.horsereality.com/") {
:root
  {
    --light-sage: #3e35663d;
    --check: #c5bbc9;
    --picture-button: #a095b7;
    --gold: #8c7dae;
    --light-brown: #3c3954;
    --light-green: #373250;
    --gold-text: #b3b7c9;
    --light-text: #b3b6c8;
    --sand: #b6a6c8;
    --body: #b7aac775;
    --green-text: #1d1c2c;
    --gold-hover: #9196af;
    --green-hover: #211d32;
    --progress: #b686ae;
    --button: #373250;
    --button-hover: #2c2742;
    --boxes: #b6a6c8;
    --white: #b6a6c8;
   
    --odd: #b7adcb;
    --even: #a095b7;
    --mine: #807499;
    --select: #bbadc8;
    --top-hover: #393552;
    --map: url(https://i.imgur.com/AQw9wYt.png);
  
    --background: url(https://i.imgur.com/PGcNt5P.jpeg);
    --title-font: "Lucida Handwriting";
    --main-font: "Big Caslon";
    --headers: url(https://cdn.discordapp.com/attachments/987518421442891878/991894994774396968/amandiltop.png);
  }
}

@-moz-document url-prefix("https://www.horsereality.com/") {
:root
  {
    --light-sage: #3e35663d;
    --check: #c5bbc9;
    --picture-button: #a095b7;
    --gold: #8c7dae;
    --light-brown: #3c3954;
    --light-green: #373250;
    --gold-text: #b3b7c9;
    --light-text: #b3b6c8;
    --sand: #b6a6c8;
    --body: #b7aac775;
    --green-text: #1d1c2c;
    --gold-hover: #9196af;
    --green-hover: #211d32;
    --progress: #b686ae;
    --button: #373250;
    --button-hover: #2c2742;
    --boxes: #b6a6c8;
    --white: #b6a6c8;
   
    --odd: #b7adcb;
    --even: #a095b7;
    --mine: #807499;
    --select: #bbadc8;
    --top-hover: #393552;
    --map: url(https://i.imgur.com/AQw9wYt.png);
   
    --background: url(https://i.imgur.com/PGcNt5P.jpeg);
    
        
    --title-font: "Lucida Handwriting";
    --main-font: "Big Caslon";
  
   
    --horse-bg: url(https://i.imgur.com/O26hK0w.png);
   
  }
}

@-moz-document url-prefix("https://v2.horsereality.com/user") {
.side-nav
  {
    padding: 10px;
    background-color: var(--light-sage);
   
    height: 800px;
    border: 2px solid var(--gold);
    border-radius: 0px 0px 20px 20px;
  }

  .side-nav ul .subtitle
  {
    font-family: var(--title-font);
    font-size: 18px;
    margin-bottom: 5px;
    line-height: 21px;
    font-weight: 900;
    text-transform: uppercase;
    color: var(--green-text);
  }

  .side-nav ul a
  {
    font-size: 15px;
    font-family: var(--title-font);
    font-weight: 900;
    text-transform: capitalize;
    color: var(--green-text);
    padding: 0;
  }

  .side-nav ul a:hover
  {
    color: var(--gold-hover);
    text-decoration: none;
  }

  .main-content
  {
    background-image: var(--body-image);
    background-color: var(--body);
    padding: 15px;
  }

  .show-profile .profile-account
  {
    width: 250px;
    padding: 10px;
    background-color: var(--sand);
    background-image: var(--player);
   
    border: none;
    border-radius: 20px 20px 20px 20px;
    margin-bottom: 20px;
  }

  .show-profile .profile-account h2
  {
    background-image: var(--headers);
    border: 2px solid var(--gold);
    border-radius: 20px 20px 20px 20px;
    font-size: 14px;
    font-family: var(--title-font);
    font-weight: 600;
    color: var(--gold-text);
    margin: 0;
  }

  .sales .horse-name
  {
    font-weight: 600;
    color: var(--green-text);
  }

  .header-menu
  {
    background: var(--top-hover);
    background-image: var(--top);
    border-bottom: 2px solid var(--gold);
  }

  .show-profile .navbar
  {
    background-color: var(--light-green)!important;
    background-image: var(--center);
    border-top: 2px solid var(--gold);
    border-bottom: 2px solid var(--gold);
    padding-top: 0;
    padding-bottom: 0;
    min-height: 40px!important;
  }

  .show-profile .navbar-avatar .navbar-avatar-container
  {
    border: 6px solid transparent;
    padding: 20px;
  }

  .header-menu nav .main-menu .user-nav>a:hover
  {
    color: var(--light-text);
  }

  .header-menu nav .main-menu .user-nav .dropdown-menu
  {
    left: -135px!important;
    min-width: 12rem!important;
    background: var(--green-hover);
    border: 2px solid var(--gold);
    border-radius: 0px 0px 20px 20px;
    opacity: 95%;
  }

  .header-menu nav .main-menu .user-nav .dropdown-menu .dropdown-item:hover
  {
    background: 0 0;
    color: var(--light-text);
  }

  .header-menu nav .stats a
  {
    color: var(--light-text);
  }

  .header-menu nav .stats
  {
    text-align: right;
    color: var(--light-text);
    font-family: var(--main-font);
    font-size: 13px;
  }

  .header-menu nav .main-menu .user-nav>a
  {
    color: var(--light-text);
    font-family: var(--title-font);
    font-weight: 900;
    font-size: 17px;
  }

  .header-menu nav .main-menu ul li a
  {
    font-size: 17px;
    font-family: var(--title-font);
    color: var(--light-text);
    font-weight: 900;
  }

  .show-profile .profile-account .tag-trial-chat
  {
    background-color: var(--light-sage);
    display: inline;
  }

  .show-profile .profile-account .tags-outer
  {
    font-weight: 500;
    color: var(--green-text);
    font-size: 14px;
    border-radius: 7px;
    padding: 1px 6px;
    white-space: nowrap;
  }

  p
  {
    font-family: var(--main-font);
    font-weight: 400;
    font-size: 14px;
    line-height: 18px;
    color: var(--green-text);
  }

  body.background.africa
  {
    background-image: var(--background);
  }

  body.background.asia
  {
    background-image: var(--background);
  }

  body.background.australia
  {
    background-image: var(--background);
  }

  body.background.europe
  {
    background-image: var(--background);
  }

  body.background.south-america
  {
    background-image: var(--background);
  }

  body.background.north-america
  {
    background-image: var(--background);
  }

  .show-profile .profile-name-and-estate .profile-username
  {
    font-size: 32px!important;
    opacity: 40%;
    font-family: var(--title-font);
    font-weight: 700!important;
    color: var(--light-text);
    margin-bottom: 5px!important;
  }

  .show-profile .profile-ranking-container .profile-rank-position
  {
    font-size: 26px!important;
    font-family: var(--title-font);
    text-align: right;
    font-weight: 700!important;
    color: var(--light-text);
    margin-bottom: 5px!important;
  }

  .footer
  {
    background: var(--light-brown);
    background-image: var(--footer);
    color: var(--gold);
    padding-top: 10px;
    padding-bottom: 10px;
    vertical-align: middle;
    margin-bottom: 50px;
  }

  .show-profile .navbar .nav-link
  {
    padding: 8px 20px;
    line-height: 24px;
    font-weight: 900;
    font-family: var(--title-font);
    color: var(--light-text);
    font-size: 16px;
  }

  .show-profile .navbar .nav-link:hover
  {
    background-color: var(--green-hover);
    cursor: pointer;
    border: none;
    border-radius: 20px 20px 20px 20px;
    color: var(--light-text);
    text-decoration: none;
  }

  .show-profile .profile-account .tag-npc, .show-profile .profile-account .tag-player
  {
    font-size: 13px;
    font-family: var(--title-font);
    color: var(--green-text);
    display: block;
    text-align: left;
    font-weight: 400;
    padding-left: 0;
  }

  body
  {
    margin: 0;
    font-family: var(--main-font);
    font-size: 13px;
    font-weight: 400;
    line-height: 1.5;
    color: var(--green-text);
    text-align: left;
  }

  .show-profile .profile-ranking-container .profile-rank
  {
    text-align: left;
    margin-bottom: 0;
    font-size: 12px;
    font-family: var(--title-font);
    line-height: 18px;
    font-weight: 400;
    color: var(--light-text);
  }

  .show-profile .profile-name-and-estate .profile-estate
  {
    font-size: 18px;
    font-family: var(--title-font);
    line-height: 21px;
    font-weight: 400;
    color: var(--light-text);
    opacity: 50%;
  }

  .header-menu nav .main-menu ul li a:not(.btn):hover
  {
    text-decoration: none;
    color: var(--light-text);
  }

  .progress-bar
  {
    display: -ms-flexbox;
    display: flex;
    -ms-flex-direction: column;
    flex-direction: column;
    -ms-flex-pack: center;
    justify-content: center;
    color: var(--gold);
    text-align: center;
    white-space: nowrap;
    background-color: var(--progress);
    transition: width .6s ease;
  }
}

