css:
@-moz-document url(https://chat.openai.com/) {
.dark .dark\:bg-gray-800{
  background-color: #131313;
}
.dark .dark\:bg-\[\#444654\]{
  background-color: #161616;
}
.dark .dark\:md\:bg-vert-dark-gradient{
  background-image: linear-gradient(180deg, #131313 0%,#131313 0%);
  padding-top: 2.3rem;
  border-top: 1px solid #282828;
}
.dark .btn-neutral{
  font-size: 0;
  line-height: 0;
  width: auto;
  padding: 20px;
  position: absolute;
  left: 102%;
  padding-right: 15px;
  top:  13%;
  background-color: #212121;
  border: none;
  box-shadow: 3px 3px 4px rgb(0,0,0,.15);
}
.dark .btn-neutral:hover{
  background-color: 535353;
}
.dark .dark\:bg-gray-700{
  background-color: #212121;
  color: #fff;
  box-shadow: 3px 3px 4px rgb(0,0,0,.15);
}
.bg-gray-900{
  background-color: rgb(0,0,0);
}
.from-gray-900{
  display: none;
}
.bg-gray-800{
  background-color: #fff;
  color: #000;
}
.from-gray-800{display: none;}
}
