css:
@-moz-document domain("youtube.com") {
  /* Change the YouTube header to pink */
  #masthead-container {
    background-color: #ff69b4;
  }

  /* Change the color of the YouTube logo to white */
  #logo-icon {
    fill: #fff;
  }

  /* Change the background color of the sidebar to pink */
  #guide {
    background-color: #ff69b4;
  }

  /* Change the color of the sidebar links to white */
  #guide .guide-section .guide-item .guide-item-title .title {
    color: #fff;
  }

  /* Change the color of the sidebar links when hovered over to a darker pink */
  #guide .guide-section .guide-item:hover .guide-item-title .title {
    color: #ff1493;
  }

  /* Change the color of the video progress bar to pink */
  .ytp-progress-bar-container {
    background-color: #ff69b4;
  }

  /* Change the color of the video progress bar when hovered over to a darker pink */
  .ytp-progress-bar-container:hover {
    background-color: #ff1493;
  }
}
