@description  Better dark theme for the Roblox website
@namespace    github.com/1011025m/RobloxDarkEnhanced
@version      0.1.0
@license      MIT
@author       1011025m
@homepageURL  https://github.com/1011025m/RobloxDarkEnhanced
@updateURL    https://github.com/1011025m/RobloxDarkEnhanced/raw/master/release/RobloxDarkEnhanced.user.css
==/UserStyle== */

/*  
    Roblox Dark Enhanced for Stylus - Made by 101025m, based on "Roblox Light Enhanced" created by Reselim.
    THIS PIECE OF CODE IS UNLICENSED, BUT YOU CAN MAKE ANY MODIFICATIONS AS YOU LIKE, AS LONG AS YOU DO NOT DISTRIBUTE IT AS YOUR OWN.
*/

@-moz-document url-prefix("https://www.roblox.com") {
/* Variables, settings, & fonts */
@font-face {
    font-family: 'Montserrat';
    font-style: normal;
    font-weight: 700;
    src: local('Montserrat Bold'), local('Montserrat-Bold'), url(https://fonts.gstatic.com/s/montserrat/v12/IQHow_FEYlDC4Gzy_m8fcoWiMMZ7xLd792ULpGE4W_Y.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02BB-02BC, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2122, U+2212, U+2215;
}

body {
    /* Colors */
    --default: #2f3136;
    --lighten: #51545b;
    /* Animations */
    --hover: 100ms cubic-bezier(0.3, 0.7, 0.5, 1);
    --open: 200ms cubic-bezier(0.17, 0.67, 0.26, 0.96);
    /* Elevations */
    --layer-1: 0 1px 6px rgba(0, 0, 0, 0.2);
    --layer-2: 0 1px 8px rgba(0, 0, 0, 0.4);
    --layer-3: 0 2px 10px rgba(0, 0, 0, 0.6);
    /* Fonts */
    --montserrat: 'Montserrat', sans-serif;
}

/* Misc. */
.content {
    background: transparent !important;
}

.games-page-left {
    margin: 0 16px;
}

.section-content,
.stack-row {
    box-shadow: var(--layer-2);
    border-radius: 3px;
}

.tab-content {
    margin: 30px 0 0 0;
}

#GroupsPeopleContainer .tab-content {
    margin: 10px 0 0 0;
}

.gutter-ads .container-main {
    max-width: inherit;
}

body[data-internal-page-name="Home"] .content {
    width: 970px;
}

#Skyscraper-Abp-Left,
#Skyscraper-Abp-Right {
    display: none;
}

/* Tabs */
.nav-tabs {
    border-radius: 3px;
    overflow: hidden;
}

.rbx-tabs-horizontal .rbx-tab-heading:hover,
.rbx-tabs-horizontal .rbx-tab-heading:focus,
.rbx-tabs-horizontal .rbx-tab-heading:active,
.rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading {
    box-shadow: 0 -4px 0 0 var(--default) inset;
}

/* Header */
#header {
    background-color: var(--default);
    box-shadow: var(--layer-1);
}

.age-bracket-label-username {
    visibility: hidden;
}

.rbx-header .rbx-navbar li .nav-menu-title {
    margin: 4px 0;
}

.rbx-header .rbx-navbar li .nav-menu-title:hover,
.rbx-header .rbx-navbar li .nav-menu-title:focus {
    background: none;
}

.rbx-header .rbx-navbar li {
    cursor: pointer;
    background-color: rgba(255, 255, 255, 0);
    transition: background-color var(--hover);
}

.rbx-header .rbx-navbar li:hover {
    background-color: rgba(255, 255, 255, 0.05);
}

.rbx-header .rbx-navbar {
    display: flex;
    width: inherit;
    margin: 0 8px 0 0;
}

/* Header: search */
#navbar-search-input,
#navbar-search-btn {
    border: none;
}

#navbar-universal-search {
    box-shadow: var(--layer-2);
    width: 360px;
}

#navbar-universal-search .dropdown-menu {
    width: 100%;
    top: calc(100% - 2px);
    clip-path: inset(0 -10px -10px -10px);
    border-top: 2px white;
    border-radius: 0 0 3px 3px;
    box-shadow: var(--layer-2);
}

.rbx-navbar-search-string {
    font-weight: 400;
}

.rbx-header .rbx-navbar-search .rbx-navbar-search-option {
    transition: all var(--hover);
}

.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0 0 0 var(--lighten) inset;
}

/* Navigation */
.notification-blue {
    background-color: var(--lighten);
    border-radius: 4px;
}

/* Home */
.home-header-content .icon-bc,
.home-header-content .icon-tbc,
.home-header-content .icon-obc {
    background-image: url(https://i.imgur.com/Jo5UzO3.png);
    background-size: 100% auto;
    filter: drop-shadow(var(--layer-2));
}

.container-header h3,
.section-header h3 {
    font-size: 14px;
    font-family: var(--montserrat);
    font-weight: 700;
    letter-spacing: 4px;
    text-transform: uppercase;
    user-select: none;
    -moz-user-select: none;
    cursor: default;
}

.container-header {
    margin: 0 0 10px;
}

.container-list {
    margin: 20px 0;
}

.home-header {
    margin: 64px 0 110px 0;
    display: flex;
    align-items: center;
}

body[data-internal-page-name="Home"] .container-main:before,
body[data-internal-page-name="Home"] .container-main:after {
    position: absolute;
    top: 0;
    width: 100%;
    height: 280px;
    z-index: -10;
}

body[data-internal-page-name="Home"] .container-main:before {
    background: linear-gradient(rgba(0,0,0,0),rgba(0,0,0,1));
    box-shadow: var(--layer-1);
}

body[data-internal-page-name="Home"] .container-main:after {
    background: url(http://i.huffpost.com/gen/3167420/original.jpg); /*  https://i.imgur.com/uGeldJX.png https://i.imgur.com/eWtfMME.png */
    background-position: center;
    opacity: 0.5;
}

.home-header-content {
    display: flex;
    flex-direction: column;
    height: 100%;
}

.home-header-content h1 {
    margin: 0 0 6px;
}

.home-header-content h1 a {
    color: white;
    text-shadow: var(--layer-2);
}

.home-header .avatar {
    width: 130px;
    height: 130px;
    margin: 0 32px 0 0;
    box-shadow: var(--layer-2);
}

#HomeContainer .container-header a {
    z-index: 50;
    background: none;
    border: none;
    font-size: 0;
    position: absolute;
    width: 100%;
    height: 32px;
    left: 0;
    top: -10px;
}

#HomeContainer .container-header a:hover,
#HomeContainer .container-header a:focus {
    box-shadow: none;
}

/* Home: friends */
.home-friends .section-content {
    background: none;
    box-shadow: none;
    padding: 0;
    margin: 20px -8px;
}

.home-friends .avatar {
    box-shadow: var(--layer-2);
}

.friend-list .friend .friend-name {
    margin: 6px 0 0 0;
}

.icon-default-friendstatus,
.icon-game,
.icon-studio,
.icon-online {
    width: 32px;
    height: 32px;
}

.icon-online {
    background-position: 0 -64px;
}

.icon-studio {
    background-position: 0 -32px;
}

/* Game cards */
.game-tile {
    transition: transform var(--open);
    overflow: hidden;
}

.vote-background,
.vote-percentage {
    transition: background-color var(--open);
}

.game-card-container,
.game-card-container:hover {
    box-shadow: var(--layer-2);
}

.vote-counts {
    display: block;
    float: left;
}

.game-card .vote-mask,
.vote-thumbs-up,
.vote-thumbs-down {
    display: none;
}

.vote-container {
    width: calc(100% - 8px);
    margin: 9px 0 0 4px;
    height: 4px;
    border-radius: 2px;
    overflow: hidden;
}

.game-card-name {
    margin: 8px 4px 4px;
    width: calc(100% - 10px);
}

.game-card-name-secondary {
    font-size: 10px;
    font-family: var(--montserrat);
    font-weight: 700;
    letter-spacing: 1px;
    margin: 0 5px;
    color: black;
    text-transform: uppercase;
}

.game-card-footer {
    border: none;
    display: block;
}

.game-card-footer .creator .text-link {
    margin-left: 1px;
}

.game-card-footer .creator {
    padding: 1px 9px 2px;
}

body[data-internal-page-name="Home"] .game-cards {
    display: flex;
    justify-content: space-between;
    margin: 0 -14px;
}

body[data-internal-page-name="Home"] .game-card {
    width: 150px;
    padding: 0;
}

/* Games page */
.games-list-container {
    margin: 10px 0 0 0;
}

.games-list-container .container-header {
    margin: 0;
}

/* Game page */
body[data-internal-page-name="GameDetail"] .section-content {
    margin: 0;
}

.stack,
.section {
    margin: 30px 0 0 0;
}

.vote-mask {
    display: none;
}

.voting-panel {
    width: auto;
    flex: 1;
}

.share-rate-favorite .favorite-button-container {
    width: auto;
}

.game-main-content .game-play-buttons {
    border: none;
}

.voting-panel .users-vote .vote-details .vote-container {
    height: 4px;
}

.social-media-share {
    display: none;
}

.game-main-content .game-calls-to-action .game-buttons-container {
    height: 160px;
}

.game-main-content .share-rate-favorite {
    height: 0;
    margin: 20px 0 0 0;
    display: flex;
}

.voting-panel .users-vote {
    padding: 0 34px 0 33px;
}

.rbx-visit-button-closed:nth-child(2) {
    font-size: 0;
    position: relative;
}

.rbx-visit-button-closed:nth-child(2):before {
    content: "Private game";
    font-size: 12px;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    transform: translate(0, -75px);
    opacity: 1;
    font-family: var(--montserrat);
    font-weight: 700;
    text-align: center;
    text-transform: uppercase;
    letter-spacing: 3px;
}

/* MOD */
a:hover {
    text-decoration: none !important;
}

.game-card-thumb-container .game-card-thumb, .game-card-thumb-container, .game-card-thumb-container::before {
    border-radius: 2px 2px 0 0;
}

.game-card-container {
    background: #202020 !important;
    border-radius: 2px;
}

.game-card .game-card-info {
    position: absolute !important;
    bottom:10px;
}

.game-card-link {
    height: 100%;
}

.game-card-name, .game-card-info {
    margin-left: 5px !important;
}

.game-card-name-secondary {
    color:rgba(255,255,255,.5)
}

.avatar-card-fullbody .avatar-card-image, .avatar .avatar-card-image, .game-card-thumb-container {
    color: transparent;
    font-size: 0;
}

textarea {
    resize: vertical;
    min-height: 50px;
}

.dark-theme .layer {
    background: transparent;
}

.alert-container {
    position: fixed;
    width: 100%;
    z-index: 100;
}

.alert-info {
    color: #fff !important;
    text-shadow: var(--layer-2);
    background-color: #d24646 !important;
    transition: background-color var(--open);
    box-shadow: var(--layer-3);
}

.alert-info:hover {
    background-color: #963232 !important;
}

.rbx-header .rbx-navbar li {
    border: 0 !important;
    border-bottom: 0 !important;
}

a.navbar-brand {
    height: auto !important;
}

#header .container-fluid {
    height: 100%;
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;
    
    padding: 0 !important;
}

#header .container-fluid {
    align-items: center;
}

.rbx-header .rbx-navbar li .nav-menu-title {
    margin: auto;
}

.rbx-header .rbx-navbar-search {
    margin-top: 0;
}

#navbar-universal-search {
    margin-right: auto;
}

ul.rbx-navbar li:after {
    display: block;
    width: 100%;
    transform: scaleX(0);
    content: "";
    position: absolute;
    
    border-top: 2px solid #fff;
    transition: transform var(--hover);
}

ul.rbx-navbar li:hover:after {
    transform: scaleX(1);
}

.rbx-header .rbx-navbar-right {
    float: none !important;
}

.chat-search {
    border-radius: 0 !important;
}

.chat-friend-list .chat-friend-container {
    transition: background-color var(--hover);
}

#mCSB_1_container, #mCSB_2_container, .mCSB_dragger {
    transition: top var(--open);
}

.chat-container .chat-main {
    transition: height .3s ease-out;
}

.rbx-header .navbar-search {
    margin-top: 0 !important;
}

.btn-primary-md, .btn-primary-sm, .btn-control-md, .btn-control-sm, .btn-secondary-md, .btn-alert-md, .btn-control-xs:not(.rbx-refresh) {
    border: solid 2px #fff;
    padding: 7px;
    transition: background-color .2s, color .2s, border .2s;
}

.btn-primary-md:hover, .btn-primary-sm:hover, .btn-control-md:hover, .btn-control-sm:hover, .btn-secondary-md:hover {
    background-color: transparent !important;
    color: #fff !important;
}

.btn-alert-md {border: solid 2px #f74b52 !important;}
.btn-alert-md:hover {border: solid 2px #f5676d !important; color: #f5676d !important;}

.rbx-tab-heading {
    transition: box-shadow .2s !important;
}

.dropdown-menu li {
    transition: background-color .2s, color .2s;
}

a.btn-common-play-game-lg, .btn-growth-md:not(.btn-secondary-md) {
    transition: background-color .2s;
    border: none;
}

a.btn-common-play-game-lg:hover, .btn-growth-md:not(.btn-secondary-md):hover {
    background-color: #008758 !important;
}
}
