css:
@-moz-document domain("reasonator.toolforge.org") {

/* ==== 0- Reasonator Toolforge - Dark and Gray Wider v.1 ==== */

.container {
    margin-left: auto;
    margin-right: auto;
    padding-left: 15px;
    padding-right: 15px;
/* border: 1px solid red !important; */
}
.col-lg-9 {
    width: 55% !important;
}
.sidebar.col-md-3 {
    width: 45% !important;
}
table.sidebar-table {
    border: 0 none;
    border-collapse: separate;
    border-spacing: 0;
    margin-bottom: 10px;
    width: 90% !important;
}
.sidebar-table.table-striped>tbody>tr>td:first-of-type {
    display: inline-block !important;
    width: 270px !important;
}

/* COLOR */


body ,
#top ,
h1, h2, h3, h4, h5, h6 {
    color: silver !important;
}

.vco-timeline .vco-navigation .timenav .content .marker.active .flag .flag-content h3, 
.vco-timeline .vco-navigation .timenav .content .marker.active .flag-small .flag-content h3 {
    color: red !important;
} 

.panel-title ,
a {
color: peru !important;
}
.sidebar-table.table-striped>tbody>tr>td:first-of-type a {
color: tan !important;
}
.vco-timeline .vco-navigation .timenav .content .marker .flag .flag-content h3, 
.vco-timeline .vco-navigation .timenav .content .marker .flag-small .flag-content h3 {
    color: black !important;
}
.vco-timeline .vco-navigation .timenav .content .marker.active .flag .flag-content h3, 
.vco-timeline .vco-navigation .timenav .content .marker.active .flag-small .flag-content h3 {
    color: red !important;
}

.modal-content {
    box-shadow: 0 5px 15px rgba(242, 237, 237, 0.88) !important;
    background-color: #222 !important;
}
.form-inline button ,
.form-inline input[placeholder] ,
.panel ,
.vco-storyjs ,
body {
    background-color: #222 !important;
}

#main_content {
    box-shadow: 8px 8px 8px #333 !important;
    background-color: #222 !important;
}
.vco-timeline .vco-navigation .timenav-background {
    background-color: #333 !important;
}
.table-striped > tbody > tr:nth-child(even) {
    background-color: #222 !important;
}

#cluetip ,
.modal-body > div,
.modal-body ,
.lead ,
.autodesc ,
.sidebar.col-md-3 ,
.sidebar .panel-info {
    background-color: #333 !important;
}
.table-striped > tbody > tr:nth-child(odd) {
    background-color: #333 !important;
}

/* ==== END ==== */
}
