css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org") {
html {
background-color:#000 !important;
}
body {
background-color:#333 !important;
-moz-border-radius:20px !important;
color:#999 !important;
width:80% !important;
margin:20px auto !important;
padding:20px !important;
}
a {
color:#BBB !important;
text-decoration:none !important;
}
a:hover {
text-shadow:0 0 1px !important;
color:#FFF !important;
text-decoration:underline !important;
}
table {
background-color:transparent !important;
}
form[name="tsearch"] {
width:125% !important;
margin:0 0 0 -12.5% !important;
}
td[bgcolor="#c08000"] {
background-color:#111 !important;
padding-top:10px !important;
}
td[bgcolor="#c08000"][align="left"] {
-moz-border-radius:16px 0 0 0 !important;
padding-left:15px !important;
}
td[bgcolor="#c08000"][align="right"] {
-moz-border-radius:0 16px 0 0 !important;
padding-right:15px !important;
}
a[href="./?admin"] font {
color:#999 !important;
font-size:10px !important;
}
a[href="./?admin"] font:hover {
color:#FFF !important;
}
td[bgcolor="#f0f0f0"] {
background-color:#111 !important;
-moz-border-radius:0 0 16px 16px !important;
padding-right:15px !important;
padding-bottom:10px !important;
}
td[bgcolor="#f0f0f0"][align="left"] {
padding-left:15px !important;
-moz-border-radius:0 0 0 16px !important;
}
tr td:first-child + td[bgcolor="#f0f0f0"][align="right"] {
-moz-border-radius:0 0 16px 0 !important;
padding-right:15px !important;
}
.topnum {
color:#999 !important;
}
.smalltext {
-moz-appearance:none !important;
background-color:#111 !important;
color:#999 !important;
border-color:#999 !important;
border-width:1px !important;
-moz-border-radius:4px !important;
}
.bodytext {
background-color:#111 !important;
-moz-border-radius:20px !important;
color:#999 !important;
padding:5px 20px !important;
}
td[valign="top"]:first-child {
padding-bottom:20px !important;
}
td[valign="top"]:first-child + td {
padding:0 !important;
}
p.quote {
background-color:#111 !important;
-moz-border-radius:20px 20px 0 0 !important;
padding-left:10px !important;
padding-top:10px !important;
padding-right:10px !important;
}
p.qt {
background-color:#111 !important;
-moz-border-radius: 0 0 20px 20px !important;
padding-left:10px !important;
padding-bottom:10px !important;
padding-right:10px !important;
}
hr {
display:none !important;
}
td[bgcolor="#c08000"][width="1"] {
display:none !important;
}
form[name="tsearch"] center:last-child td.toplinks {
-moz-border-radius:16px 16px 0 0 !important;
padding:10px 0px 0px 15px !important;
}
tr:first-child td:first-child.footertext:not([bgcolor]) {
-moz-border-radius:16px !important;
text-align:center !important;
padding:10px !important;
background-color:#111 !important;
}
tr:first-child + tr td:first-child[class="footertext"][align="right"] {
background-color:#111 !important;
padding-bottom:10px !important;
text-align:center !important;
-moz-border-radius:0 0 16px 16px !important;
}
td.footertext[align="left"] {
text-align:left !important;
-moz-border-radius:0 0 0 16px !important;
padding-left:15px !important;
background-color:#111 !important;
padding-bottom:10px !important;
}
td.footertext:first-child + td {
padding-right:15px !important;
-moz-border-radius:0 0 16px 0 !important;
background-color:#111 !important;
padding-bottom:10px !important;
}
font[size="-1"] * {
margin-top:20px !important;
}
.seltext {
background-color:transparent !important;
color:#999 !important;
}
select {
background-color:transparent !important;
-moz-appearance:none !important;
-moz-border-radius:8px !important;
color:#999 !important;
}
input[type="checkbox"] {
-moz-appearance:none !important;
background-color:#111 !important;
color:#999 !important;
border-color:#222 !important;
}
textarea {
-moz-appearance:none !important;
background-color:#111 !important;
color:#999 !important;
-moz-border-radius:16px !important;
border-width:1px !important;
padding:15px !important;
}
form[name="srch"] td.bodytext {
background-color:transparent !important;
}
input.button {
-moz-appearance:none !important;
background-color:#111 !important;
color:#999 !important;
-moz-border-radius:8px !important;
}
input.button:hover {
background-color:#222 !important;
color:#FFF !important;
}
input.text {
-moz-appearance:none !important;
background-color:#111 !important;
color:#999 !important;
-moz-border-radius:8px !important;
border-width:1px !important;
padding-left:5px !important;
}
}
