css:
@-moz-document domain(forums.pokecharms.com) {
	 /*Navigation Bar Background*/
.p-nav *{
  background: #005500;
}

.p-sectionLinks {
  background: #008500;
}

.menu-content {
   background: #005500;
}

/* Nav Bar text*/ 

.p-nav a {
  color: #EEEEEE;
}

.p-sectionLinks a {
  color: #EEEEEE;
}

.menu-content a {
  color: #EEEEEE;
}

/* Body */

.p-body-inner {
  background: #212225 !important;
}

.p-title-value {
  color: #ffd1dc;
  /*color: #E2E4E9;*/
}

.block-header a {
  color: #ffd1dc;
  /*color: #E2E4E9;*/
}

.block-header {
  background: #333437;
}

.block-body {
  background: #292a2d;
}

.block-minorHeader, .block-footer {
  background:#333437;
}

/* Node Section*/
.node-main a {
  color: #009500;
}

.node-icon i:before{
  color: #009500 ;
}

.node--unread .node-icon i:before {
  color: #0000E5;
}
.node-stats {
  color: #FFFFFF;
}

/* In-Thead Message Cells */
.message-cell--user {
  background: #333437 !important;
}

.block-container {
  border: #008900;
}

.fr-element {
  background: #333437;
}

.fr-element p {
  color: #EEEEEE;
}

.message-cell--user a {
  color: #009500;
}

.message-cell {
  background: #292A2D;
  border:  solid #008900;
}

.message-content {
  color: #EEEEEE;
}

.message-userArrow {
  border-right-color: #008900;
  border-left-color: #008900;
}

.message-userArrow::after {
border: #008900;
}

.reactionsBar {
  background: #333437;
  border: #008500;
}

.block--messages .message, .block--messages .block-row {
  border: solid #008900;
}

.bbCodeBlock {
    background: #292A2D ;
}


/*User Messages*/
.message-responses * {
  background: #292A2D;
}

.input, .u-muted {
  background:  #DEF8BE;
}

.reactionSummary li {
  background: #292A2D !important;
}

.message-responseRow {
  border: solid #008500;
}

.bbWrapper {
  color: #EEEEEE;
}

.contentRow-fader {
  background: linear-gradient(to bottom, #05050500 0%,  #A9A9A9 80%);
}

.tabs {
    background: #333437;
}

/*Misc */
.menu-footer {
    background: #292A2D;
}

.menu-row-alt, .menu-row {
      background: #333437 !important;
}

.contentRow-main {
  color: #A9A9A9;
}

.contentRow {
  background: 444444;
}

.tooltip-content {
  background: #333437 !important;
}

.tooltip-content-inner, .tooltip-preview  {
  background: #333437;
}

.tootip-preview .tooltip-content-inner .tooltip-content-cover {
    background: linear-gradient(to bottom, #05050500 0%,  #A9A9A9 80%);
}

.iconic-label, .inputGroup-text {
  color: #A9A9A9;
}

tbody, tr, td {
  background: #444444 !important;
  }

blockquote {
  background: #333437 !important;
}

}
