css:
@-moz-document url-prefix(https://app.wized.com/configurator/){

    body .modal-component.style-width-16-25.style-top-left-5.data-field {
        width: 90vw;
        height: 84vh;
    }

    body .modal-component .data-field-left {
        width: 50%;
        height: 100%;
    }

    body .field-setting {
        width: 100% !important;
    }

    body .field-setting.textfield {
        max-height: 100%;
        height: 100% !important;
    }

    body .modal-component .panel_scroll {
        width: 50% !important;
    }

    body .modal-component .style-flex-row {
        min-height: min-content;
        height: min-content;
    }

    body .modal-component .data-field-left .style-flex-row {
        height: 60%;
    }

    body .modal-component .panel_section-header .style-flex-row p.style {
        text-overflow: none;
        max-width: 100%;
    }

    body .modal-component .data-field {
        display: flex;
        flex-direction: column;
        width: 100%;
        height: 100%;
        width: 100%;
    }

    body .modal-component .button.data-field {
        padding: 1.5rem;
        flex-direction: row;
    }

    body .modal-component .data-component {
        height: min-content;
        margin-bottom: 0.4rem;
    }

    body .modal-component .data_attribute {
        display: flex;
        align-items: start;
        height: 100%;
        width: 100%;
        height: min-content;
        padding: 0.8rem;
    }

    body .modal-component .auto-scroll.style {
        height: min-content;
        max-height: 100% !important;
        overflow: inherit;
        max-height: 40% !important;
        height: 40% !important;
    }

    body .modal-component .auto-scroll.max-5 {
        height: 40%;
        max-height: 40%;
    }

    body .modal-component .code-rows {
        height: 100%;
    }

    body .modal-component .style-bold {
        width: 100%;
        display: block;
        word-break: break-all;
    }

    body .modal-component .setting-wrapper .style-flex-row {
        align-items: start;
    }

    body .data-component {
        height: min-content;
        margin-bottom: 0.4rem;
    }

    body .data_attribute {
        display: flex;
        height: 100%;
        width: 100%;
        height: min-content;
        padding: 0.8rem;
  }

	  body .panel_wrapper .panel {
      width: 35vw;
      background-color: rgba(67, 72, 82, 0.9) !important;

	  }

	  body .panel_wrapper .panel .data-field {
    	max-width: 100%;
  }
  
  body .site_wrapper {
	   margin-left: 10rem !important;
  }




}
