css:
/* Zmiana tła na ciemne */
body {
  background-color: #222;
}

/* Zmiana koloru tekstu na jasny */
body, p, h1, h2, h3, h4, h5, h6, span, a {
  color: #fff;
}

/* Zmiana koloru linków */
a {
  color: #4af;
}

/* Zmiana koloru przycisków */
button, input[type="submit"] {
  background-color: #4af;
  color: #fff;
}

/* Zmiana koloru elementów nagłówka */
header {
  background-color: #222;
  color: #fff;
}

/* Zmiana koloru elementów stopki */
footer {
  background-color: #222;
  color: #fff;
}

