css:
@-moz-document domain(logs.blackrussia.online) {
:root {
--bs-body-bg: #000;
}
.navbar-dark .navbar-brand, .navbar-dark .navbar-brand:focus, .navbar-dark .navbar-brand:hover {
    color: #f0f;
    text-shadow: #f0f 0px 0px 10px;
}
body::-webkit-scrollbar {
  width: 14px;               /* ширина всей полосы прокрутки */
}

body::-webkit-scrollbar-track {
  background: #101;        /* цвет зоны отслеживания */
}

body::-webkit-scrollbar-thumb {
  background-color: #f0f;    /* цвет бегунка */
  border-radius: 20px;       /* округлось бегунка */
	border: 3px solid #101
}
.bg-success {
    --bs-bg-opacity: 0;
    background-color: #000;
    box-shadow: #0ff 0px 0px 10px;
}
.badge {
    border-radius: 1rem;
    color: #0ff;
    text-shadow: #0ff 0px 0px 10px;
    display: inline-block;
    font-size: .75em;
    font-weight: 500;
    line-height: 1;
    padding: 0.35em 0.65em;
    text-align: center;
    vertical-align: baseline;
    white-space: nowrap;
}
.bg-dark {
    --bs-bg-opacity: 0;
    background-color: #000;
}
h1, h2, h3, h4, h5, h6 {
    color: #f0f;
    text-shadow: #f0f 0px 0px 10px;
}
#log-table[data-v-2d76ca92], .table {
    --bs-table-bg: #101;
    --bs-table-accent-bg: transparent;
    --bs-table-striped-color: #212529;
    --bs-table-striped-bg: rgba(0,0,0,0.05);
    --bs-table-active-color: #212529;
    --bs-table-active-bg: rgba(0,0,0,0.1);
    --bs-table-hover-color: #212529;
    --bs-table-hover-bg: rgba(0,0,0,0.075);
    border-color: #dee2e6;
    color: #fff;
    text-shadow: #000 0px 0px 20px;
    margin-bottom: 1rem;
    vertical-align: top;
    width: 100%;
		border-width: 0;
}
body {
    -webkit-text-size-adjust: 100%;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    color: var(--bs-body-color);
    background: linear-gradient(90deg, rgb(34, 0, 34), rgb(0, 34, 34), rgb(34, 0, 34));
    font-family: var(--bs-body-font-family);
    font-size: var(--bs-body-font-size);
    font-weight: var(--bs-body-font-weight);
    line-height: var(--bs-body-line-height);
    margin: 0;
    text-align: var(--bs-body-text-align);
}
.td-index[data-v-2d76ca92] {
    background-color: #404!important;
    color: #fff;
    text-shadow: #fff 0px 0px 10px;
}
.td-transaction-desc[data-v-2d76ca92] {
    font-size: 1.2rem;
    font-weight: 300;
   	background: linear-gradient(135deg, rgb(34, 0, 34), rgb(0, 34, 34), rgb(34, 0, 34));
}
.td-player-name[data-v-2d76ca92] a[data-v-2d76ca92] {
    color: #0ff;
    text-decoration: none;
}
.td-category[data-v-2d76ca92] a[data-v-2d76ca92] {
    color: #f0f;
    font-style: italic;
    text-decoration: none;
}
#log-table[data-v-2d76ca92] .first-row[data-v-2d76ca92] td[data-v-2d76ca92] {
    border: 1px solid #101;
    text-align: center;
}
#log-table[data-v-2d76ca92] .second-row[data-v-2d76ca92] td[data-v-2d76ca92] {
    padding: 0.5rem 0.5rem 0.5rem 1.5rem;
    border-bottom: 1px solid #f0f;
		border-left: 1px solid #202;
		border-right: 1px solid #202;
}
.navbar-dark .navbar-nav .nav-link {
    color: #0ff;
}
.bi-arrow-left::before {
    content: "\f12f";
    color: #0ff;
}
.bi-sort-down::before {
    content: "\f575";
    color: #0ff;
}
.show-filter-btn[data-v-2d76ca92] {
    background: #0aa;
    color: #000;
    border: 0;
    border-bottom-left-radius: 25px;
    height: 44px;
    opacity: 1;
    position: fixed;
    right: 0;
    top: 0;
    z-index: 1059;
}
#log-filter-section[data-v-2d76ca92] {
    background: linear-gradient(180deg, rgb(34, 0, 34), rgb(0, 34, 34));
    border: 1px solid #000;
    border-radius: 25px;
    height: 830px;
    margin-left: 1rem;
    min-width: 20rem;
    overflow-y: auto;
}
.multiselect-option {
    align-items: center;
    box-sizing: border-box;
    cursor: pointer;
    background: linear-gradient(90deg, rgb(34, 0, 34), rgb(0, 34, 34));
    color: #f0f;
    display: flex;
    font-size: var(--ms-option-font-size,1rem);
    justify-content: flex-start;
    line-height: var(--ms-option-line-height,1.375);
    padding: var(--ms-option-py,.5rem) var(--ms-option-px,.75rem);
    text-align: left;
    text-decoration: none;
}
.multiselect {
    align-items: center;
    background: var(--ms-bg,#fff);
    border: 1px solid #0ff;
    border-radius: var(--ms-radius,4px);
    color: #0ff;
    box-sizing: border-box;
    cursor: pointer;
    display: flex;
    font-size: var(--ms-font-size,1rem);
    justify-content: flex-end;
    margin: 0 auto;
    min-height: calc(var(--ms-border-width, 1px)*2 + var(--ms-font-size, 1rem)*var(--ms-line-height, 1.375) + var(--ms-py, .5rem)*2);
    outline: none;
    position: relative;
    width: 100%;
}
.multiselect-search {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background: #101;
    color: #f0f;
    border: 0;
    border-radius: var(--ms-radius,4px);
    bottom: 0;
    box-sizing: border-box;
    font-family: inherit;
    font-size: inherit;
    left: 0;
    outline: none;
    padding-left: var(--ms-px,.875rem);
    position: absolute;
    right: 0;
    top: 0;
    width: 100%;
}
.multiselect .multiselect-option.is-pointed.is-selected {
    background: linear-gradient(90deg, rgb(68, 0, 68), rgb(0, 68, 68));
    color: #f0f;
}
.multiselect-option.is-selected.is-pointed {
    background: var(--ms-option-bg-selected-pointed,#26c08e);
    color: var(--ms-option-color-selected-pointed,#fff);
}
.multiselect .multiselect-option.is-pointed {
    background: linear-gradient(90deg, rgb(68, 0, 68), rgb(0, 68, 68));
    color: #f0f;
}
.multiselect .multiselect-option.is-selected {
    background: linear-gradient(90deg, rgb(51, 0, 51), rgb(0, 51, 51));
    color: #f0f;
}
.multiselect-option.is-selected {
    background: var(--ms-option-bg-selected,#10b981);
    color: var(--ms-option-color-selected,#fff);
}
.multiselect-option.is-pointed {
    background: var(--ms-option-bg-pointed,#f3f4f6);
    color: #f0f;
}
.multiselect-dropdown {
    -webkit-overflow-scrolling: touch;
    background: var(--ms-dropdown-bg,#fff);
    border: 1px solid #0ff;
    border-radius: 0 0 var(--ms-dropdown-radius,4px) var(--ms-dropdown-radius,4px);
    bottom: 0;
    display: flex;
    flex-direction: column;
    left: calc(var(--ms-border-width, 1px)*-1);
    margin-top: calc(var(--ms-border-width, 1px)*-1);
    max-height: 15rem;
    outline: none;
    overflow-y: scroll;
    position: absolute;
    right: calc(var(--ms-border-width, 1px)*-1);
    transform: translateY(100%);
    z-index: 100;
}
.autoComplete_wrapper>input {
    background-color: #101;
    background-origin: border-box;
    background-position: left 1.05rem top 0.8rem;
    background-repeat: no-repeat;
    background-size: 1.4rem;
    border: 1px solid #0ff;
    border-radius: 4px;
    box-sizing: border-box;
    color: #0ff;
    font-size: 1rem;
    height: 2.45rem;
    margin: 0;
    outline: none;
    padding: 0 1rem;
    text-overflow: ellipsis;
    transition: all .4s ease;
    width: 100%;
}
.autoComplete_wrapper>input:focus {
    border: 1px solid #0ff;
    color: #0ff;
    background-color: #303;
}
.autoComplete_wrapper>input:hover {
    color: #0ff;
    transition: all .3s ease;
}
.autoComplete_wrapper>ul>li {
    background-color: #101;
    color: #0aa;
    font-size: 1rem;
    margin: 0;
    overflow: hidden;
    padding: 0.3rem 0.5rem;
    text-align: left;
    text-overflow: ellipsis;
    transition: all .2s ease;
    white-space: nowrap;
}
.autoComplete_wrapper>ul>li mark {
    background-color: transparent;
    color: #0ff;
    font-weight: 700;
}
.autoComplete_wrapper>ul {
    background-color: #202;
    border: 1px solid #0ff;
    border-radius: 2px;
    box-shadow: 0 3px 6px hsl(210deg 8% 62% / 15%);
    box-sizing: border-box;
    left: 0;
    list-style: none;
    margin: 0.5rem 0 0;
    max-height: 226px;
    outline: none;
    overflow-y: scroll;
    padding: 0;
    position: absolute;
    right: 0;
    transition: opacity .15s ease-in-out;
    z-index: 10;
}
.form-control {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background-clip: padding-box;
    background-color: #101;
    border: 1px solid #0ff;
    border-radius: 0.25rem;
    color: #0ff;
    display: block;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 0.375rem 0.75rem;
    transition: border-color .15s ease-in-out,box-shadow .15s ease-in-out;
    width: 100%;
}
.input-group>.form-control, .input-group>.form-select {
    flex: 1 1 auto;
    min-width: 0;
    position: relative;
    width: 1%;
    background-color: #101;
    color: #0ff;
}
.input-group-text {
    align-items: center;
    background-color: #066;
    border: 1px solid #0ff;
    border-radius: 0.25rem;
    color: #0ff;
    display: flex;
    font-size: 1rem;
    font-weight: 400;
    line-height: 1.5;
    padding: 0.375rem 0.75rem;
    text-align: center;
    white-space: nowrap;
}
#log-filter-form__transaction-desc {
background-color: #101;
color: #0ff
}
#next-page-btn[data-v-2d76ca92], .btn-secondary, .close-btn, .icon-btn, .show-filter-btn {
    background-color: #066;
    border-color: #0ff;
    color: #0ff;
}
#next-page-btn[data-v-2d76ca92]:hover, .btn-secondary:hover, .close-btn:hover, .icon-btn:hover, .show-filter-btn:hover {
    background-color: #099;
    border-color: #0ff;
    color: #0ff;
}
.close-btn, .icon-btn, .show-filter-btn {
    align-items: center;
    border-radius: 0 25px 0 25px;
    cursor: pointer;
    display: flex;
    height: 46px;
    justify-content: center;
    opacity: .6;
    width: 46px;
}
.dp__theme_light {
    --dp-background-color: #011;
    --dp-text-color: #0ff;
    --dp-hover-color: #0aa;
    --dp-hover-text-color: #0ff;
    --dp-hover-icon-color: #0ff;
    --dp-primary-color: #1976d2;
    --dp-primary-disabled-color: #6bacea;
    --dp-primary-text-color: #f8f5f5;
    --dp-secondary-color: #055;
    --dp-border-color: #ddd;
    --dp-menu-border-color: #ddd;
    --dp-border-color-hover: #aaaeb7;
    --dp-disabled-color: #000;
    --dp-scroll-bar-background: #f3f3f3;
    --dp-scroll-bar-color: #959595;
    --dp-success-color: #76d275;
    --dp-success-color-disabled: #a3d9b1;
    --dp-icon-color: #0ff;
    --dp-danger-color: #ff6f60;
    --dp-marker-color: #ff6f60;
    --dp-tooltip-color: #fafafa;
    --dp-disabled-color-text: #8e8e8e;
    --dp-highlight-color: rgb(25,118,210);
}
.dp__input {
    background-color: #101;
    border: 1px solid #0ff;
}
#log-filter[data-v-2d76ca92] .form-label[data-v-2d76ca92] {
    color: #099;
    font-weight: 500;
}
.dropdown-menu {
    background-clip: padding-box;
    background-color: #044;
    border: 1px solid #0ff;
    border-radius: 0.5rem;
}
.lookup-comment[data-v-2d76ca92] {
    color: #0ff;
    font-size: .9rem;
    font-weight: 400;
}
.dropdown-item:focus, .dropdown-item:hover {
    background-color: #066;
    color: #0ff;
}
.btn-outline-danger {
    border-color: #f00;
    color: #fff;
    background-color: #300;
}
.btn-primary, .submit-btn {
    background-color: #044;
    border-color: #0ff;
    color: #0ff;
}
#loading-overlay-heading[data-v-173ec149] {
    color: #f0f;
    font-size: 2rem;
    font-weight: 500;
    letter-spacing: 1px;
    padding: 1rem;
    text-align: center;
    text-transform: uppercase;
}
#loading-overlay[data-v-173ec149] .spinner[data-v-173ec149] {
    border-width: 0.375rem;
    color: #f0f;
    height: 4rem;
    width: 4rem;
}
#loading-overlay-container[data-v-173ec149] {
    align-items: center;
    display: flex;
    flex-direction: column;
    background-color: #101;
    height: 100%;
    justify-content: center;
    width: 100%;
}
.bg-dark {
    --bs-bg-opacity: 1;
    background: linear-gradient(135deg, rgb(34, 0, 34), rgb(0, 34, 34), rgb(34, 0, 34));
}
#placeholder-msg[data-v-9c1e68e2] {
    color: #f0f;
    font-size: 1.25rem;
    padding: 1rem;
    text-align: center;
}
#placeholder-pic[data-v-9c1e68e2] {
    display: block;
    opacity: 0;
    margin: auto;
    max-height: 20rem;
}
#content-placeholder[data-v-9c1e68e2] {
 		background-image: url(https://imgur.com/9XN2j2i.png);
    background-repeat: no-repeat;
    background-size: contain;
    background-position: center;
}
}
