@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("serials.ws") {

html>body>center>table>tbody>tr>td+td>div,
html>body>center>table>tbody>tr>td+td+td,
html>body>center>table>tbody>tr+tr,
html>body>center>table>tbody>tr>td+td>br,
a[target*="_blank"],a[target*="_BLANK"],
a[href*="warezstore.com"],
a[href*="softwarelist.us"],
a[href*="view.php?id="],
hr[color="#99ccff"],
html>body>center>table>tbody>tr>td>b>b,
html>body>center>table>tbody>tr>td+td>b,
html>body>center>table>tbody>tr>td>b>br,
html>body>center>br{
display: none !important;
}
html>body>center>table>tbody>tr>td+td[width="50%"] {
width: 70% !important;
}
html>body>center>table>tbody>tr>td+td>center {
margin-top: 5px !important;
}
html>body>center>table>tbody>tr>td>table[width="250"] {
position: relative !important;
top: 8px !important;
}
html>body>center>table>tbody>tr>td>b>table {
position: relative !important;
top: 8px !important;
}
}
