css:
#conteudo{
 margin-bottom: 0px !important;
 overflow-x: hidden !important;
}

h4.kanban-list-title.tooltips {
 margin-bottom: 0px !important; 
}

.portlet{
 margin-bottom: 10px !important;
}

#sortable_portlets{
 left: 0px !important; 
}

ui.page-breadcrumb.breadcrumb{
 display: none !important; 
}

small.hidden-xs {
 padding-left: 5px !important; 
}

div.header.navbar.navbar-inverse.navbar-fixed-top {
  display: none !important;
}

div.page-content {
 height: 100vh !important; 
}

div.footer {
 display: none !important; 
}

div#sortable_portlets.row.ui-sortable.col-md-12.ui-draggable {
 height: 75vh !important; 
}

column.sortable.no-padding.ui-sortable {
 height: 75vh !important; 
}

.page-header-fixed .page-container {
 margin-top: 0px !important;
}

.column {
 height: 70vh !important;
}

/* Estilos gerais para o scrollbar */
body {
  --sb-track-color: #ffffff;
  --sb-thumb-color: #4c4c4c;
  --sb-size: 7px;
}

/* Estilos para divs com scrollbar */
div#id_lista_33 {
  scrollbar-color: var(--sb-thumb-color) var(--sb-track-color);
}

div#id_lista_33::-webkit-scrollbar {
  width: var(--sb-size);
}

div#id_lista_33::-webkit-scrollbar-track {
  background: var(--sb-track-color);
  border-radius: 1px;
}

div#id_lista_33::-webkit-scrollbar-thumb {
  background: var(--sb-thumb-color);
  border-radius: 1px;
}
