css:
@-moz-document domain('roblox.com'), url-prefix('https://roblox.com') {/* Scrollbar */
::-webkit-scrollbar {
  width: 3px;
}
/* Track */
::-webkit-scrollbar-track {
  background: black !important;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: #8888887c;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
  background: #888888cc;
}

body {
    background: black !important;
    font-family: HCo Gotham SSm,Helvetica Neue,Helvetica,Arial,Lucida Grande,sans-serif;
    font-weight: 300;
    font-size: 16px;
    width: 100%;
    height: 100%;
    min-width: 320px;
    margin-bottom: 168px;
    overflow-y: scroll;
}
.container-main{
    background-color: #000 !important;
}
.dark-theme .content {
    background-color: #121212 !important;
    color: #fff;
    border-radius: 12px;
    margin-top: -1.5rem;
    max-width: calc(100vw - 13.5vw);
    margin-left: 13vw;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    flex-direction: column-reverse;
  	padding-bottom: 40vh;
}
.home-container {
    float: left;
    width: 100%;
    max-width: 970px;
}

.dark-theme .rbx-left-col {
    background-color: #121212 !important;
    margin-top: -1.5rem;
    border-radius: 12px;
    min-width: 12.5vw;
    max-height: 88.5vh;
}
.dark-theme .container-footer {
    background-color: transparent;
  	height: 35vh;
  	margin-bottom: 22vh;
  	max-width: 85%;
}

.chat-container.collapsed .chat-main {
    height: 34px;
    display: none;
}
#skip-to-main-content{display:none;}
.dark-theme .rbx-header {
    background-color: black;
    border-radius: 0px 0px 0px 0px;
    margin-top: 90vh;
    height: 10%;
  	
}
.container-fluid {
    margin-right: auto;
    margin-left: auto;
    padding-left: 0;
    padding-right: 0;
    margin-top: calc(2.5% - 1%);
}

.home-container h1{
  visibility: hidden;
  position: relative;
  padding-bottom: 10%;
}
.home-container h1:after{
    visibility: visible;
    position: absolute;
    top: 25%;
    left: 25%;
  	font-size: 3rem;
    content: "Home - Roblox";
}
.rbx-header .rbx-navbar-header {display: none;}
.rbx-header .rbx-navbar {
    margin: 4px auto;
    display: flex;
    width: 20vw;
}
#navbar-search-input{
  background-color: #212121;
  border: none;
}
.dark-theme .rbx-header .navbar-search {
    background-color: #212121;
  margin-left: 1vw;
  width: 60.5vw;
}
.rbx-header .rbx-navbar-icon-group .age-bracket-label .age-bracket-label-username {
    display: none;
}
.gotham-font .font-footer, .gotham-font .font-footer:active, .gotham-font .font-footer:focus, .gotham-font .font-footer:hover, .gotham-font .font-footer:link, .gotham-font .font-footer:visited, .gotham-font .text-footer, .gotham-font .text-footer:active, .gotham-font .text-footer:focus, .gotham-font .text-footer:hover, .gotham-font .text-footer:link, .gotham-font .text-footer:visited, .gotham-font .xsmall, .gotham-font .xsmall:active, .gotham-font .xsmall:focus, .gotham-font .xsmall:hover, .gotham-font .xsmall:link, .gotham-font .xsmall:visited, .gotham-font .xxsmall, .gotham-font .xxsmall:active, .gotham-font .xxsmall:focus, .gotham-font .xxsmall:hover, .gotham-font .xxsmall:link, .gotham-font .xxsmall:visited {
    font-size: 10px;
    font-weight: 500;
}
.rbx-header .rbx-navbar-icon-group .age-bracket-label .dynamic-overflow-container .avatar-headshot-xs {
    width: 35px;
    height: 35px;
    margin-right: 6px;
    flex-shrink: 0;
	  margin-top: -3px;
}
.dark-theme .avatar .avatar-card-image {
    background-color: transparent;
  	border: 1px solid #212121;
}
.dark-theme .rbx-header .navbar-search .new-dropdown-menu {
    border-radius: 12px;
    margin-top: -100vh;
    transform: translate(0%, 50%);
    left: -6px;
    width: calc(100% + 12px);
    background-color: #121212;
    box-shadow: 0 0px 20px 9px rgba(0,0,0,.3);
  	border: 1px solid #212121;
}
#Skyscraper-Abp-Left{
  display: none;
}
.dark-theme .section-content {
    background-color: #212121;
    border-radius: 8px;
    color: #fff;
}
.dark-theme .rbx-tabs-horizontal .nav-tabs {
    background-color: #212121;
    color: #fff;
}
.dark-theme .rbx-tab .rbx-tab-heading {
    background-color: #212121;
    color: #fff;
}
.profile-avatar-mask{
  background-color: #212121;
}
.dark-theme .profile-avatar-right .profile-avatar-mask {
    background-color: #212121;
    color: #fff;
    border-radius: 8px;
}
.dark-theme .avatar-container .avatar-card-image, .dark-theme .avatar-container img {
    background-color: #212121;
  border: 1px solid #323232
}
.dark-theme .slide-switcher.groups {
    background-color: #212121;
    color: #fff;
    border-radius: 12px;
}

.dark-theme .slide-switcher .slide-item-container .slide-item-container-right {
    background-color: #212121;
    color: #fff;
    border-radius: 12px;
}
.dark-theme .slide-switcher .slide-item-container .slide-item-container-left {
    background-color: #161616;
    border-radius: 12px 0px 0px 12px;
}
.dark-theme .asset-thumb-container {
    background-color: transparent;
    border: 1px solid #212121;
}
.dark-theme .thumbnail-2d-container {
    background-color: transparent;
    border: 1px solid #212121;
}
.rbx-left-col li .text-nav .avatar-headshot-xs {
    width: 35px;
    height: 35px;
    margin: 2px;
    flex-shrink: 0;
}
.rbx-divider{
  width: 95%;
}
.dark-theme .alert-warning, .light-theme .alert-warning {
    display: none;
}
.dark-theme .alert-success, .light-theme .alert-success {
    display: none;
}
#games-carousel-page{
  margin-left: 2.5%;
  width: 95%;
}
.dark-theme .horizontal-scroller .scroller .spacer {
    background-color: transparent;
}
.scroller .arrow {
    background-repeat: no-repeat;
    height: 100%;
    width: 27px;
    position: absolute;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    background-color: #010101;
}
.dark-theme .scroller.disabled {
        background-color: #010101;
    border: none;
    box-shadow: 5px 6px 5px 1px black;
}
.horizontal-scroller .scroller{
  background-color: #010101;
}
.horizontal-scroller .scroller:hover{
    background-color: #010101;
}
#catalog-container{
    margin-left: 5%;
  	width: 94%;
}
.dark-theme .item-card-container .item-card-thumb-container {
    border: 1px solid #343434;
    background-color: #212121;
}
}
