@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("esea.net"), domain("esportsea.com") {

/* Header Background */
body {background: #f2f3f7 url('http://www.rockdown.net/935271Untitled2.png') repeat-x right top !important}

/* Qu0te Editz */
.comment-quote {
background-color:#e1e1e1 !important; 
padding-bottom:15px !important; 
padding-top:5px !important;
padding-left: 25px !important; 
color: #606060 !important;
background-image: url('http://www.rockdown.net/eseaq.png') !important;
background-repeat: no-repeat !important;
background-position:top left !important;}

/* Layout Ed1t */
div.layout-standard {background-image: url('http://rockdown.net/esealayout-standard_bg.png') !important;}
div#layout-ticker {background-image:url('http://rockdown.net/ticker_bg.png') !important;}

/* h1dden Modules */
#layout-navigation, .ticker-text, #layout-game-selector, #module-whos-playing, #module-league, #module-events-small, #module-ads-skyscraper, #module-ads-support, #body-downloads-other, #module-recent-downloads, #module-recruit, #module-promotions, #layout-container #layout-ticker form, #layout-container #layout-ticker .ticker-date {visibility:hidden !important; display:none !important;}

#layout-container #layout-ticker {height:10px !important; }
#layout-container {margin-top:23px !important;  border: 2px solid #dbdbdb !important; border-top:none !important; }
#layout-header    {margin-top:-20px !important; width:100% !important; }

/* Visible Modules */
#module-your-activity { visibility:visible !important;}

/* Module Edits */
#module-recent-discussion { padding-top:5px !important;}
#layout-ticker {margin-bottom:-15px !important;}
#layout-header {margin-bottom:1px !important;}

/* Emoticons */
img[src$="/images/emoticons/1.gif"]{margin-bottom:-6px !important; height:0 !important;width:20px !important;padding-top:20px !important;background:url(http://rockdown.net/ESEAMotes/smile.gif) no-repeat !important}
