@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("profileedit.myspace.com") {
h3,font,#footer,#topnav,#header, #home_additionalLinks, #home_userURLInfo, #home_setHomePage, #home_searchAddressBook, #splash_coolNewPeople,#splash_profile,#home_schools,#home_coolNewVideos,#ctl00_Main_ctl00_ProfileHome_gads,#ctl00_Main_SplashDisplay_ctl01_signUpHyperLink,#home_greybox,#home_featured_music,#home_featured_books,#home_featured_filmmaker,#home_featured_comedy,#ctl00_Main_ctl00_CMS_Tom_Announcement,#ctl00_Main_SplashDisplay_SplashHeader,#ctl00_Main_SplashDisplay_userVideos_VideoToday,#splash_videotoday,#splash_greybox,#ad300x100,#splash_music,#ctl00_Main_SplashDisplay_featuredVideos_CMS_videos,#ctl00_Main_SplashDisplay_CMS_SplashHome_Gads_DE, #splash_mainBottom{display:none !important;}
#details_interests div{left:-150px !important;border:0px !important;background-color:transparent !important;border:0px !important;border-style:none !important;}
#details_interests input div{left:0px !important;border:0px !important;background-color:transparent !important;border:0px !important;border-style:none !important;}
#details_interests input{left:0px !important;border:0px !important;background-color:transparent !important;border:0px !important;border-style:none !important;}
body{background-color:#ffffff !important;}
h1,h2,h3,h4,h5{color:#000000 !important;background-color:#FFFFFF !important}
table,td,tr,div,div input, input div, input, #details_interests,textarea{background-color:transparent !important;border:0px !important;border-style:none !important;}
a,a:link,a:visited{border:0px !important;color:#000000 !important;background-color:transparent !important;text-decoration:none !important;}
a:hover,a:active{border:0px;color:#CCCCCC !important;background-color:transparent !important;text-decoration:none !important;}
#content {width:750px !important;left:0px !important; top:0 !important; margin:0 !important; padding:0 !important; text-align:center !important;min-height:1200px !important; height:1800px !important}
	#profileEdit_tabs {padding:0px 0 0px 0 !important;margin: 0 !important; border-bottom:0px solid transparent !important;background-color:#FFFFFF !important; text-align:center !important;}
	#profileEdit_tabs ul {list-style: none !important; margin: 0 !important; padding:0 !important;float:left !important;}
	#profileEdit_tabs li {list-style: none !important; margin: 0 !important; padding: 0px 0 0px !important; padding:0 !important; display:none !important; background-color:transparent !important;}
	#profileEdit_tabs a {background-color:transparent !important; padding: 0px 0px 0px 0px !important; margin-left: 0px !important; text-decoration: none !important; font-weight:normal !important; font-family:Segoe UI !important; font-size:10px !important;}
	#profileEdit_tabs li a:link { color: #000000 !important; }
	#profileEdit_tabs li a:visited { color: #FFFFFF !important; }
	#profileEdit_tabs li a:hover { color:#FFFFFF !important;}
	#profileEdit_tabs .tabActive{padding-bottom:0px !important; padding:0 !important; background-color:transparent !important;}
	#profileEdit_tabs .tabActive a{padding-bottom:0px !important; color: #FFFFFF !important; background-color:transparent !important;}
	#details_tabs {float:left !important;margin:0 !important; padding:0 !important}
	#details_tabs li {border-right:0px none transparent !important; float:left !important; list-style:none !important;}
	#details_tabs a {font-weight:normal !important;}
	.seperator {display:none !important;border-top:0px none transparent !important;border-bottom:0px none transparent !important; color:#FFFFFF !important; width:100% !important}
	.row label{float:left !important; width:0px !important; text-align:center !important; margin-right:0% !important;position:absolute;left:-225px !important; font-weight:normal !important;}
	.row{clear:none !important;}
	.saveButtons {float:left !important; margin-top:0px !important}
	.w500 {width:1000px !important;height:550px !important;}
	.row {text-align:left !important;}
	.row input {float:left !important;}
	.gender {float:right !important;}
	.gender input, .gender label {float:left !important;display:none; clear:none !important;font-weight:normal !important; text-align:left !important;}
        textarea{width:750px !important; height:1800px !important;}
	.gender input {width:20px !important;}
	#body .cols, #body .cols th, #body .cols td {border:0px none transparent !important;}
	#body .cols td {border:0px none transparent !important;vertical-align:top !important;}
	#body .cols th, #body .cols td {padding:0px 0px !important;}
	.simpleHeadings {margin-bottom:0px !important; text-align:left !important}
        div {background-color:transparent !important;}
	.section {border:0px none transparent !important;}
	.heading {background-color:transparent; color:#FFFFFF; line-height:200% !important; text-align:left !important}
}
