css:
@-moz-document url-prefix("https://www.amazon.co.jp/")
{
 
  
  body
  {
    background: url(https://i.pinimg.com/564x/38/5c/68/385c6892a1b2a5903749cb723a4dee85.jpg) center top fixed !important;
    background-size: cover !important;
    background-repeat: no-repeat !important;
  }

  #nav-belt
  {
    background-color: rgba(0,0,0,0.3) !important;
  }

  #nav-main
  {
    background-image: none;
    background-color: rgba(0,0,0,0.3) !important;
  }

  #navFooter.navLeftFooter
  {
    background-color: rgba(0,0,0,0.5) !important;
  }

  #navFooter.navLeftFooter .navFooterDescLine
  {
    background-color: rgba(0,0,0,0.2) !important;
  }

  #navFooter.navLeftFooter .navFooterBackToTop
  {
    background-color: rgba(0,0,0,0.3) !important;
  }

  .nav-a
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  #nav-search .nav-search-field
  {
    background: rgba(255,255,255,0.9) !important;
  }

  .gw-ftGr-desktop-hero .gw-hero-image .cropped-image-map-size img, .gw-ftGr-desktop-hero .image-map .cropped-image-map-size img
  {
    height: 300px !important;
    width: 1500px !important;
    opacity: 0.7 !important;
  }

  .departmentTitle
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .a-section > div.a-section.a-spacing-medium.gw-flex-row-container h1
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .a-section > div.a-section.a-spacing-medium.gw-flex-row-container h3
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .a-section > div.a-section.a-spacing-medium.gw-flex-row-container span
  {
    color: #fff !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  #navbar.nav-sprite-v1.nav-bluebeacon
  {
    background-color: rgba(0,0,0,0.1) !important;
  }

  .seemore a
  {
    color: #eee !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  #searchTemplate > div#rightContainerATF
  {
    background: rgba(255,255,255,0.9) !important;
  }

  #rhf-container > div.rhf-border
  {
    background: rgba(255,255,255,0.9) !important;
  }

  #s-result-info-bar-content
  {
    background: rgba(255,255,255,0.9) !important;
  }

  #navFooter.navLeftFooter .navFooterCopyright
  {
    background-color: transparent !important;
    max-width: 100%;
    margin: 0;
    padding: 15px 0 15px !important;
  }

  h1 > div.gbh1-bold
  {
    color: #eee !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  .gb-header > div.gbhcopy
  {
    color: #eee !important;
    text-shadow: 1px 1px 1px #000 !important;
  }

  #a-page > div > div
  {
  }

  #container.lp.s-padding-left-small.twoColumn > div.a-section
  {
    background: rgba(255,255,255,0.8) !important;
  }

  #a-page > div.a-fixed-left-flipped-grid.s-padding-left-small.s-padding-right-small.s-span-page.a-spacing-top-small > div.a-fixed-left-grid-inner > div.a-fixed-left-grid-col.a-col-left > div.a-section.s-padding-right-mini.s-border-right
  {
    background: rgba(255,255,255,0.8) !important;
  }

  #a-page > div#unrec-pageContent.a-section.a-spacing-none > div#gw-content-grid.a-row > div.a-section > div.a-section.a-spacing-medium.gw-flex-row-container
  {
    background-color: rgba(0,0,0,0.5) !important;
  }

  #leftNav.s-padding-right-mini.s-border-right
  {
    background: rgba(255,255,2555,0.8)  !important;
  }

  #nav-subnav
  {
    background: rgba(0,0,0,0.5) !important;
  }

  #a-page > div > div#dp-container.a-container
  {
    background: rgba(255,255,2555,0.8)  !important;
  }
}
