/* ==UserStyle==
@name           Modern Coat
@namespace      userstyles.world
@version        1.0.0
==/UserStyle== */


@-moz-document domain("animesuge.to") {
/* Simple accent. by skyflair */
  


* {
    font-family: 'Montserrat', 'Andale Mono', sans-serif;
}
:root {
    -main-accent: #f00;
    nd-accent: #c22;
}
body {

  background: url(https://cdn.discordapp.com/attachments/958891693342613504/1120943946034581644/ghostie_heebie_wallpaper_sideways.jpg) 20% 24%;
;
    background-size: 100%;
    background-attachment: scroll
}
.fa,
.fas {
    color: var(nd-accent)
}
.fa:hover,
.fas:hover {
    color: #fff;
}
#hsearch > div form input {
  background: url(https://cdn.discordapp.com/attachments/958891693342613504/1120943946034581644/ghostie_heebie_wallpaper_sideways.jpg) 20% 24%;
    color: var(--main-accent);
    border-radius: 20px;
}

#hsearch {
    background: url(https://cdn.discordapp.com/attachments/958891693342613504/1120943946034581644/ghostie_heebie_wallpaper_sideways.jpg) 20% 24%;
    background-size: 100%;
    background-repeat: no-repeat;
    height: 180px;
    position: relative;
    margin-bottom: 100px;
    border-radius: 20px
}
#disqus_thread p {
    color: #ff0202!important;
}
#dsq-content {
    color: #ffffff !important;
}
.inner > img {
    border-radius: 20px;
}

.poster {
    border-radius: 10px;
}
}
