/* ==UserStyle==
@name         jn.pt - Destaques mais pequenos
@namespace    USO Archive
@version      20230622.00.33
@description  - Redução do tamanho dos destaques
@author       gdx
@license      NO-REDISTRIBUTION==/UserStyle== */
@-moz-document domain("www.jn.pt") {
.sk1440-3x2 {
    flex: 0 0 66.666666%;
    max-width: 66.666%;
    max-height: 400px;
    margin-bottom: 1.5rem;
}

.sk-dark-theme .ITC001 .media {
    position: relative;
    max-height: inherit;
}

.jn001-container .ITC001, .jn001-container .PC001 {
    margin-bottom: 1.5rem;
    max-height: inherit;
}

.clean-link {
    color: inherit;
    cursor: pointer;
    text-decoration: none;
    max-height: inherit;
}

.embed-responsive {
    display: block;
    overflow: hidden;
    padding: 0;
    position: relative;
    width: 100%;
    max-height: inherit;
}

.sticker-col .sk-wrapper .HC002 {
    margin-bottom: 0.75rem;
    max-height: 126px;
}

.sk1440-3 {
    flex: 0 0 33.333333%;
    max-width: 33.333333%;
    max-height: 400px;
    margin-bottom: 1.5rem;
}

.HC002 .media {
    flex: 0 0 calc(40% + 1rem);
    margin-right: 1rem;
    max-height: inherit;
}
    
.sk1440-4 {
    flex: 0 0 25%;
    max-width: 25%;
    margin-bottom: 1.5rem;
    max-height: 300px;
}
    
    
.PC001 .media {
    position: relative;
    flex: 0 0 50%;
    width: 100%;
    max-height: inherit;
}    
    
container .PC001 {
    margin-bottom: 1.5rem;
    max-height: inherit;
}
    
.jn001-container {
    padding-bottom: 2.5rem;
    padding-top: 3.5rem;
}  

    
}
