css:
@-moz-document domain("youtube.com"){

.html5-video-player {
    position: relative;
    width: 100%;
    height: 100%;
    overflow: hidden;
    z-index: 0;
    outline: 0;
    font-family: "YouTube Noto",Roboto,Arial,Helvetica,sans-serif;
    color: #eee;
    text-align: left;
    direction: ltr;
    font-size: 11px;
    line-height: 1.3;
    -webkit-font-smoothing: antialiased;
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    -ms-touch-action: manipulation;
    touch-action: manipulation;
    -ms-high-contrast-adjust: none;
    border-radius: 15px;
  }
.ytp-swatch-background-color, .ytp-swatch-background-color-secondary {
background-color: #ffb831ff ! important;
}
.ytp-volume-slider-handle {
background: #ffb831ff ! important;
}
.ytp-volume-slider-handle:before {
background: #ffb831ff ! important;
}
.ytp-big-mode .ytp-volume-slider-handle:after {
left: 18px ! important;
}
.ytp-volume-slider-handle:after {
left: 12px ! important;
}
.ytp-swatch-color {
color: #ffb831ff ! important;
}
.ytp-settings-button.ytp-hd-quality-badge:after, .ytp-settings-button.ytp-4k-quality-badge:after, .ytp-settings-button.ytp-5k-quality-badge:after, .ytp-settings-button.ytp-8k-quality-badge:after {
background-color: #ffb831ff ! important;
}
.ytp-chrome-controls .ytp-button[aria-pressed]:after {
background-color: #ffb831ff ! important;
}
circle[fill="#d90000"] {
fill: #ffb831ff ! important;
}
.ytp-chrome-controls .ytp-button.ytp-youtube-button:not([aria-disabled=true]):not([disabled]):hover .ytp-svg-fill-logo-tube-lozenge {
fill: #ffb831ff ! important;
}
.ytp-live-badge[disabled]:before {
background: #ffb831ff ! important;
}
.ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg, .ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg {
fill: #ffb831ff ! important;
}
.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
background: #ffb831ff ! important;
}
.ytp-contextmenu .ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox {
background: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIGhlaWdodD0iMTAwJSIgdmVyc2lvbj0iMS4xIiB2aWV3Qm94PSIwIDAgMjQgMjQiIHdpZHRoPSIxMDAlIj48cGF0aCBkPSJNOSAxNi4yTDQuOCAxMmwtMS40IDEuNEw5IDE5IDIxIDdsLTEuNC0xLjRMOSAxNi4yeiIgZmlsbD0iI2ZmZiIgLz48L3N2Zz4=) ! important;
}
.ytp-settings-button.ytp-3d-badge:after {
background-color: #ffb831ff ! important;
}

/* Mini YouTube support */
.mnyt-progress-wrap {
background-color: #ffb831ff ! important;
}
#mnyt-progress-time:hover {
background-color: #ffb831ff ! important;
}
.mnyt-play-button:hover {
background-color: #ffb831ff ! important;
}
.mnyt-size-button:hover {
background-color: #ffb831ff ! important;
}
.mnyt-size-button:hover {
background-color: #ffb831ff ! important;
}
.mnyt-progress-pointer {
background-color: #ffb831ff ! important;
}
}
