css:
@-moz-document domain('www.readwn.com'){
:root{
  --th-bgcolor: #232323;
  --th-bgcolor2: #121212;
  --th-bordercolor: #282828;
  --th-anchorcolor: #acacac;
  --th-textcolor: #ddd;
  --th-textcolor2: #acacac;
  --th-linkcolor: /*[[linkcolor]]*/;
}
/*
body{
  background-color: var(--th-bgcolor);
}*/
}
@-moz-document regexp('(?:http://|https://)(?:www.|)readwn.com/novel/.-*[a-zA-Z0-9].{0,}'){
body.nightmode{
  background-color: var(--th-bgcolor);
  color: var( --th-textcolor);
}
body.nightmode .main-header,
body.nightmode .navigation-bar,
body.nightmode ul.navbar-menu li a
{
  background-color: var(--th-bgcolor2);
  border-color: var(--th-bordercolor);
	color: var(--th-textcolor);
}
body.nightmode #chapter-article{
  max-width: 902px;
  margin: 0 auto;
}
body.nightmode .content-wrap{
  max-width: 902px;
  margin: 0;
  padding: 0;
}
body.nightmode .action-items{
  display: none;
}
body.nightmode .chapter-header{
  background-color: var(--th-bgcolor2);
  border: 2px solid var(--th-bordercolor);
  border-width: 0 2px 2px;
  padding: 24px 48px;
}
body.nightmode .titles h1 a{
  color: var( --th-linkcolor);
  font-size: 20px;
  font-weight: 600;
}
body.nightmode .titles h2{
  color: var( --th-textcolor);
  font-size: 18px;
}
body.nightmode .chapter-content{
  background-color: var(--th-bgcolor2);
  border: 2px solid var(--th-bordercolor);
  border-width: 0 2px 2px;
  padding: 12px 48px;
}
body.nightmode .chapter-content p{
  font-size: 16px;
}
body.nightmode .chapternav{
  background-color: var(--th-bgcolor2);
  border: 2px solid var(--th-bordercolor);
  border-width:  0 2px 2px;
  padding: 24px 48px 24px;
}
body.nightmode #chapter-article .recommends{
  border-width: 0;
}
}
