css:
@-moz-document domain(brick-hill.com) {
.theme-default .new-theme .button.blue:not([disabled]) {
    background: #007CDC;
    border: 1px solid #005cdc;
    transition: background ease-in .12s, border ease-in .12s, border-color ease-in .12s;
}
.theme-default .new-theme .button.blue:not([disabled]):hover {
    background: #008cff;
    border-color: #007cff;
}
.theme-default .new-theme .button.blue:active {
    background: #005bb;
    border-color: #004bb;
}
nav div ul li a {
    display: inline-block;
    padding: 8px 5px;
    background: transparent;
    transition: background ease-in .12s;
    border-bottom: none;
}
nav div ul li a:hover {
    background: #212121;
}
.theme-default .new-theme button.green:not([disabled]) {
    background: #00b71c;
    border: 1px solid #00a71c;
    transition: background ease-in .12s, border ease-in .12s, border-color ease-in .12s;
}
.theme-default .new-theme button.green:not([disabled]):hover {
    background: #00c71c;
    border-color: #00b71c;
}
.theme-default .new-theme button.green:active {
    background: #00a71c;
    border-color: #00971c;
}
body {
  background: linear-gradient(to right, #fafafa, #fff, #fafafa);
}
  nav div.primary {
    background: #007CDC;
    padding: 6px 0;
  }
nav .primary div ul li a:hover {
    background: #005cdc;
}
nav .primary div ul li a {
    border-radius: 5px;
}
nav .info {
    background: #005cdc;
}
.alert.error {
  background: #cf0000;
}
  .red-text {
    color: #aa0000;
  }
}
