css:
@-moz-document domain(brick-hill.com) {

    .button.blue, .register-button, button.free, button.blue, button.blue[disabled]:hover {
    background: linear-gradient(#0044ff, #00afff) !important;
    background-size: 100% 50px !important;
    border-radius: 32px;
    box-shadow: 0 1px inset #ffffff44;
    background-position: bottom !important;
    transition: background-position .1s;
    border: 1px solid #0044ff !important;
    text-shadow: 0 -1px #00000033
    }

    .button.blue:hover, .register-button:hover, button.free:hover, button.blue:hover {
    background-position: top !important;
    }

    button.blue[disabled] {
    opacity: 0.4;
    }

    .register-button {
    padding-left: 16px;
    padding-right: 16px;
    }
    
    button.free {
        border-radius: 32px !important;
    }

    button.green, button.bucks.width-100, .button.bucks, .button.green {
    background: linear-gradient(#0e7100, #0eff00) !important;
    background-size: 100% 50px !important;
    border-radius: 32px;
    box-shadow: 0 1px inset #ffffff44;
    background-position: bottom !important;
    transition: background-position .1s;
    border: 1px solid #0e7100 !important;
    text-shadow: 0 -1px #00000033
    }

    button.green:hover, button.bucks:hover, .button.bucks:hover, .button.green:hover {
    background-position: top !important;
    }

    button.play-button {
    /* big ass button */
    background-size: 100% 100px !important;
    }

    .button.yellow, button.orange, button.bits, button.yellow, .button.bits {
    background: linear-gradient(#f40, #ffae00) !important;
    background-size: 100% 50px !important;
    border-radius: 32px;
    box-shadow: 0 1px inset #ffffff44;
    background-position: bottom !important;
    transition: background-position .1s;
    border: 1px solid #f40 !important /* to fix wardrobe button being cut off */;
    text-shadow: 0 -1px #00000033;
    text-overflow: clip !important;
    }

    .button.yellow:hover, button.orange:hover, button.bits:hover, button.yellow:hover, .button.bits {
    background-position: top !important;
    }
    
    .download button.orange {
    background-size: 100% 150px !important;
    border-radius: 48px;
    }

    .cancel-button {
    background: linear-gradient(#848484, #fff) !important;
    background-size: 100% 50px !important;
    border-radius: 32px !important;
    box-shadow: 0 1px inset #ffffff44;
    background-position: bottom !important;
    transition: background-position .1s;
    border: 1px solid #848484 !important /* to fix wardrobe button being cut off */;
    text-shadow: 0 -1px #00000033;
    text-overflow: clip !important;
    }

    .cancel-button:hover {
    background-position: top !important;
    }

    .play-button[disabled] {
    background: linear-gradient(#0e7100, #0eff00) !important;
    background-size: 100% 50px !important;
    border-radius: 32px;
    box-shadow: 0 1px inset #ffffff44;
    background-position: bottom !important;
    transition: background-position .1s;
    border: 1px solid #0e7100 !important;
    text-shadow: 0 -1px #00000033;
    opacity: 0.4;
    background-size: 100% 100px !important;
    }

    .tab-button:hover {
        border-radius: 32px !important;
    }
    
    .tab-button.blue, .tab-button.blue:hover {
        background: #006fff !important;
        box-shadow: none;
        border: none !important;
    }
    
    .membership-button {
    background: linear-gradient(#0e7100, #0eff00) !important;
    background-size: 100% 200px !important;
    border-radius: 64px;
    box-shadow: 0 1px inset #ffffff44;
    background-position: bottom !important;
    transition: background-position .1s;
    border: 1px solid #0e7100 !important;
    text-shadow: 0 -1px #00000033
    }
    
    .clear {
        border-radius: 32px !important;
    }
}
