css:
@-moz-document domain("youtube.com") {
  /* Fullscreen */
  .ytp-fullscreen .ytp-spinner-circle {
    border-width: 6px !important;
  }

  .ytp-fullscreen .ytp-spinner {
    display: none;
  }

  .ytp-fullscreen .ytp-spinner:before,
  .ytp-fullscreen .ytp-spinner-rotator:after {
    content: "";
    height: 50px;
    width: 50px;
    background: url(https://media.tenor.com/SDwGg31pp4AAAAAC/maxwell-the-cat-maxwell.gif) no-repeat;
    background-size: 50px;
    position: absolute;
  }

  .ytp-fullscreen .ytp-spinner:before {
    height: 300px;
    width: 300px;
    margin-left: 10.5px;
    margin-top: -50px;
    background-size: 110px;
  }

  .ytp-fullscreen .ytp-spinner-rotator:after {
    margin-left: 121px;
    margin-top: 15px;
    transform: rotate(0) scale(1);
  }
}
