css:
@-moz-document url-prefix('https://fhqx.hcmute.edu.vn/mod') {
.page-header-wrapper.has-course-header-image
{ 
  height: 100%;
}
.course-header-bg
{
  height: 0px !important;
}

}

@-moz-document domain('fhqx.hcmute.edu.vn') {
/* CONNECT ERROR LABEL MESSAGE */
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd
{
  border-bottom: 0px;
}
.moodle-dialogue-base .moodle-dialogue-wrap
{
  background-image: url(https://images7.alphacoders.com/388/388716.jpg);
  background-size: cover;
  background-position: 15px -30px;
}
[class="yui3-widget yui3-panel moodle-dialogue yui3-widget-positioned yui3-widget-modal yui3-widget-stacked moodle-has-zindex moodle-dialogue-focused"]
{
  left: 499px !important;
}
[class ="moodle-dialogue-hd yui3-widget-hd"] h1#moodle-dialogue-undefined-header-text
{
  visibility: hidden;
  color: white;
}
[class ="moodle-dialogue-hd yui3-widget-hd"] h1#moodle-dialogue-undefined-header-text:after
{
  visibility: visible;
  color: black;
  content: "WEB QUÁ LỎD DM WEB TRƯỜNG ?? \A An error occurred whilst communicating with the server";
  position: absolute;
  top: 40%;
  left: 40px;
	white-space: pre-wrap;
  text-align: center;
  -webkit-text-fill-color: white;
  -webkit-text-stroke: 0.1px;
}

}
@-moz-document domain('fhqx.hcmute.edu.vn') {

/* MAIN MENU SLIDES */
ul[class = "slides"] [class *="slide slide-3"]
{
  background-image: url(https://images8.alphacoders.com/116/1163434.jpg);
}
ul[class = "slides"] [class *="slide slide-2"]
{
  background-image: url(https://images8.alphacoders.com/108/1086826.jpg);
}
ul[class = "slides"] [class *= "slide slide-1"]
{
  background-image: url(https://images3.alphacoders.com/809/809945.jpg);
}

@-moz-document domain('fhqx.hcmute.edu.vn') {
/*	BG IMAGE	*/
.page-wrapper.has-bg-image
{
  background-image : url(https://images3.alphacoders.com/968/968265.png);
}
/*	ICON	*/
.has-logo .login-sitename a, .has-logo .login-sitename a:focus
{
  background-image :  url(https://i.imgur.com/jZWt7yY.png) !important;
	background-size: 50% !important;
  background-blend-mode: darken;
}
/*	White Panel	*/
.auth-blocks-wrapper
{
  background: rgb(0,0,0,0.5);
}

/* Danger	*/
.alert-danger
{
 visibility: hidden;
  color: white;
  background-color: #cc5555;
  border-color: transparent;
}
.alert-danger:after
{
  visibility: visible;
  content:" GET IN THE ROBOT SHINJI, LOG BACK AGAIN!!	--------	DM WEB TRƯỜNG!! ?? ";
  color: white;
  background-color: #cc5555;
  border-color: transparent;
  padding: .75rem 1.25rem;
  position: relative;
  margin-left: -280px;
  border: 1px solid transparent;
  width: 505px;
  border-radius: .25rem;
}

/* TEXT COLOR */
.auth-blocks-wrapper .forgetpass a
{
  color: rgb(255,255,255, 0.9);
}
.auth-blocks-wrapper .rememberpass
{
  color: rgb(255,255,255, 0.9);
}

}


}
