css:
@-moz-document domain("gosupermodel.com")
  {

body.normal {
    margin: 0;
    background: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/landingpage_bk.jpg) repeat top left #996600;
    }

.loginpage_background {
    background: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/landingpage.png) no-repeat -27px 0;
}

.signup {
    position: absolute;

    top: 101px;
    left: 934px;
    height: 179px;
    width: 253px;
    background: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/language_specific/signupnow_en.gif) no-repeat 0 0;
}

.signup:hover {
    background: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/language_specific/signupnow_en.gif) no-repeat 0 -179px;
}

.billboard {
    background: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/billboard_frame.png) no-repeat 0 0;
    position: absolute; 
    
    height: 460px;
    width: 500px;
    cursor: pointer;
    z-index: 2;
}

div.framework_top {
  background-image: url(https://a4c9m5w9.stackpathcdn.com/images/topbar/top_bar_brandgirls.jpg);
}
    
div.loginpage_full.loginpage_background fieldset a, div.loginpage_full.loginpage_background img {
    border: 0;
    background-image: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/language_specific/vip_en.gif);
    height: 0px;
    padding: 0px 149px 164px;
    background-repeat: no-repeat;
}

div.loginpage_full.loginpage_background .billboard_content img {
    border: 0;
    background-image: none;
    height: unset;
    padding: unset;
    background-repeat: no-repeat;
}
    

.landingpage_full body, html {
    height: inherit;

    background: url(https://a4c9m5w9.stackpathcdn.com/images/loginpage/spt_2014/landingpage_bk.jpg) repeat top left #996600;
}
}
