css:
@-moz-document domain("roblox.com") {
/* Profile Page Base Code by DittoDino/BoopBop */
/*Colour of the Boxes*/
.section-content.profile-header-content.ng-scope, .rbx-tabs-horizontal .nav-tabs *, .profile-avatar-mask, .section.layer.profile-collections.ng-scope, .switcher *, .section.profile-statistics .section-content
   {
     background-color:#7cc0f4!important;
   }
.section-content.profile-header-content.ng-scope *, .rbx-tabs-horizontal .nav-tabs *, .profile-container .asset-item, .switcher *, .section.profile-statistics .section-content *, .profile-about *
   {
     color:black!important; 
   }
/*End of the Boxes*/
/*Profile Avatar Viewer*/
.profile-avatar-left
   {
     background:transparent;    
   }
/*End of the Avatar Viewer*/
/* Profile Page Code Ends here*/
/* Catalog Page Base Code by DittoDino/BoopBop*/
/*Creator Text*/
.creator-name.text-link, .text-robux-tile
   {
     color:black!important;    
   }
/*End of Creator*/
/*Topics*/
.topic.ng-binding.ng-scope
   {
     background-color:#7cc0f4!important;
     color:black!Important;  
   }
/*End of Topics*/
/*Search Filters*/
.search-options.ng-scope *, .text-link.breadcrumb-link.ng-binding
   {
     color:black!important;    
   }
/*End of Search Filters*/
/*Category Options Dropdown*/
.input-dropdown-btn.category-options.ng-scope
   {
     color:black!Important;
     background-color:#7cc0f4!important;
   }
/*End of Options Dropdown*/
/*Catalog Searchbar*/
.catalog-container .search-container .input-group .search-input, .catalog-container .search-container .input-group .autocomplete-dropdown-menu .dropdown-menu
   {
     background-color:#fffafae3!important;    
     border-color: #808080!important;
     border-radius:5px!important;
     color: #7cc0f4!important;
   }
/*End of Searchbar*/
/*Item Description*/
.item-details *:not(.btn-growth-lg)
   {
     color:black!important;
   }
/*End of Description*/
/*Item Comment Section*/
.section-content.AddAComment 
   {
     background-color:#7cc0f4!important; 
   }
/*End of Comment Section*/
/*Price Chart*/
.section-content.price-volume-charts-container
   {
     background-color:#7cc0f4!important; 
   }
/*End of Price Chart*/
/*Catalog Code Ends here*/
/* Game Page Code */
/*Voting/Favourite/Follow Panel*/
.favorite-follow-vote-share *
   {
     color:black!important;
   }
/*End of Panel*/
/*Game Description*/
.text.game-description.linkify, .age-rating-details.col-xs-12.section-content *, .age-recommendation-title
   {
     color:black!important;
     font-weight:550;
   }
/*End of Description*/
/*Game Stats*/
.border-top.border-bottom.game-stats-container.follow-button-enabled 
   {
     background-color:#0009!important;
   }
.border-top.border-bottom.game-stats-container.follow-button-enabled *
   {
     color:black!important;
     font-weight:1000;
   }
/*End of Game Stats*/
/*Private Servers/Badges*/
.create-server-banner.section-content.remove-panel *
   {
     color:black!important; 
     border-color:black!important;
     font-weight:600;
   }
.stack-row.badge-row.ng-scope, .badge-image *, .para-overflow.ng-binding, .badge-stats-container *, .btn-full-width.btn-control-sm.ng-binding
   {
     background-color:#7cc0f4!important; 
     color:black!Important;
   }
.server-list-section *, button.btn-more.rbx-refresh.refresh-link-icon.btn-control-xs.btn-min-width
   {
     color:black!important; 
   }
/*End of Private Server/Badges*/
/*Gamepasses*/
.container-list.game-dev-store.game-passes *
   {
     color:black!important;
   }
.store-card-footer
   {
     background-color:#7cc0f4!important;
     border-radius:10px;
   }
/*End of Gamepasses*/
/*Games Page Code Ends Here*/
/* Global Base Code by DittoDino/BoopBop*/

/*Ads be gone*/
#navigation:after {	position: absolute !important; 	content: "Updated Base Code by @dinodabtuf" !important;	bottom: 5px !important;	left: 10px !important;	font-size: 8px !important;} .footer-note:after{content: " Hey there, this is a little secret from BoopBop/DittoDino or @dinodabtuf, the creator of this style's base code you're using."} .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard, .abp .UpsellAdButton, .abp .UpsellAdButton:link, .abp .UpsellAdButton:visited, .abp .BadAdButton, .abp .BadAdButton:link, .abp .BadAdButton:visited, .abp .ad-identification-text, a.ad img, .abp, .profile-ad, #TopAbpContainer, .ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer, #Skyscraper-Adp-Right, #Skyscraper-Adp-Left, .abp-spacer, #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even, #GamesListContainer1 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd, #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-even, #GamesListContainer8 > div.games-list > ul > div.abp.in-game-search-ad.dynamic-ad.ad-order-odd, .sponsoblack-game, .alert-info, #mCSB_1_container > ul > li.font-bold.small, #mCSB_1_container > ul > li.rbx-nav-sponsor,.banner-container, #dialogs-minimize /* This base code was made by DittoDino aka BoopBop, this is a form of cblackits to indicate who copied this code. */
   {
     visibility: hidden;
     background-color:transparent;
   } 

/*End of Ads*/

/*Colour Customisation (I don't use American spelling)*/
/* Header*/
.rbx-navbar *, .rbx-header
   {
     color:black !important; 
     background-color:#7cc0f4!important;
     border-color:#7cc0f4!important;
   }
/*End of Header*/
/*Top Searchbar*/
.navbar-search *, .rbx-header .navbar-search .new-dropdown-menu 
   {
     background-color:#414040c9!important;    
     border-color: #808080!important;
     border-radius:5px!important;
     color: #7cc0f4!important;
   }
/*End of Searchbar*/
/*Sidebar*/
#navigation
   {
     background-color:#7cc0f487!important;    
   }
.left-col-list *:not(.notification)
   {
     color:black!important;    
   }
.left-col-list *:not(#upgrade-now-button) /*Yeah it's bad but stylish ain't allowing it otherwise.*/
   {
     color:black!important;    
   }
/*End of Sidebar*/
/*Footer*/
.container-footer
   {
     background-color:#7cc0f4!important;
     color:black!important;
     position:static;
   }
#footer-container *
   {
     color:black!important;  
   }
/*End of Footer*/
/*Chat*/
.chat-body, .chat-friend-info *, .chat-windows-header, .dialogs *
   {
     background-color:#7cc0f4!important;
     color:black!important;
   }
/*End of Chat*/
/*Dropdown Menus*/
.dropdown-menu, .place-container, .people-info-card-container .interaction-item,  #notification-stream-container *
   {
     background-color:#7cc0f4!important;  
     color:black!important;
   }
/*End of Dropdowns*/
/*Header Text*/
h1, h2, .game-card-name, .item-card-name, .friend-name, .btn-secondary-xs.btn-more.see-all-link-icon.ng-binding, .text-name, .text-robux-lg, .btn-secondary-xs.see-all-link-icon.btn-more
   {
     color:black!important;    
   }
/*End of Headers*/
/*Like/dislike ratio and player count*/
.vote-percentage-label, .playing-counts-label
   {
     color:black!important;    
   }
/*End of ratio and player count*/
/*Colour Customizaton Ends*/

/*Background*/
body, .content
   {
     background:transparent!important;
   }
html
   {
     background-position: inherit;
     width: 100% !important;
     height: 100% !important;
     /* background-image: url("https://art.ngfiles.com/images/459000/459102_xperiaz1_this-my-second-steven-universe-br.jpg?f1477565172")*/
     background-size: cover !important;
     background-attachment: fixed !important;
     background-image:url(https://i.pinimg.com/736x/65/17/25/6517256b3bee298e8b3d8a88a801ce5c.jpg) 
 }
/*End of Background*/
/*Global Code Ends Here*/
}
