css:
@-moz-document url-prefix("https://discord.com")
{
/* ------BACKGROUND------ */
  body
  {
    background: url "https://cdn.discordapp.com/attachments/1123740420124450847/1126260428989804615/61fY3YrexrL._AC_UF8941000_QL80_.jpg" !important;
  }

  .appMount-3lHmkl
  {
    background: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260428989804615/61fY3YrexrL._AC_UF8941000_QL80_.jpg";
  }

/* ------REMOVE GREY/WHITE BG COLORS------ */
  .theme-dark,
.theme-light
  {
    -background-primary: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260645201981471/TrollFace.webp";
    -background-secondary: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260645201981471/TrollFace.webp";
    -background-secondary-alt: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260865155481740/istockphoto-499870283-612x612.jpg";
    -background-tertiary: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260865155481740/istockphoto-499870283-612x612.jpg";
    -background-floating: "https://cdn.discordapp.com/attachments/1123740420124450847/1126261089559134260/trollface_1.webp";
    -channeltextarea-background: "https://cdn.discordapp.com/attachments/1123740420124450847/1126261461690351636/Troll-Face-Meme-Smile-Carnival.jpg_960x960.webp";
    -deprecated-panel-background: "https://cdn.discordapp.com/attachments/1123740420124450847/1126261461690351636/Troll-Face-Meme-Smile-Carnival.jpg_960x960.webp";
    -background-modifier-hover: "https://cdn.discordapp.com/attachments/1123740420124450847/1126261655223935048/d0c3a97ac999685eeff6023e40c12e3f.jpg";
    -background-modifier-selected: "https://cdn.discordapp.com/attachments/1123740420124450847/1126261655223935048/d0c3a97ac999685eeff6023e40c12e3f.jpg";
  }

/* ------POP-UP------ */
  .modal-yWgWj-,
.footer-3rDWdC
  {
    background: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260428989804615/61fY3YrexrL._AC_UF8941000_QL80_.jpg" !important;
  }


/*remove borders*/
  .header-2o-2hj,
.searchBar-6Kv8R2,
.content-yTz4x3::before
  {
    box-shadow: none !important;
  }

/*misc*/
  .container-1D34oG,  /*friends list*/
.scrollbar-2rkZSL,  /*scrollbar*/
.pad-29zQak,
.track-1JN30G
  {
    background: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260428989804615/61fY3YrexrL._AC_UF8941000_QL80_.jpg" !important;
  }

/* ------SCROLLBAR------ */
  .scrollbar-2rkZSL
  {
    width: 8px;
  }

  .thumb-2JwNFC
  {
    background: "https://cdn.discordapp.com/attachments/1123740420124450847/1126260428989804615/61fY3YrexrL._AC_UF8941000_QL80_.jpg" !important;
  }

  .track-1JN30G,
.thumb-2JwNFC
  {
    border: none !important;
  }

/* ------EMBEDDED TEXT------ */
  .embedProvider-3k5pfl,
.embedDescription-1Cuq9a,
.embedFields-2IPs5Z,
.embedAuthorName-3mnTWj,
.embedFooterText-28V_Wb
  {
    color: #f2f2f2 !important;
  }

  .cozy-3raOZG .messageContent-2qWWxC, .compact-T3H92H .header-23xsNx .username-1A8OIy, .cozy-3raOZG .header-23xsNx .username-1A8OIy
  {
    text-shadow: 0 0 20px black;
  }
}
