css:
@-moz-document url-prefix("http://forum.6502.org") {
img[src*="forum_"] {
    width: 0;
    height: 0;
    padding-left: 46px;
    padding-top: 25px;
}

img[src*="forum_unread.gif"] {
    background-image: url(data:image/png;base64,);
}

img[src*="forum_read.gif"] {
    background-image: url(data:image/png;base64,);
}

img[src*="topic_"],
img[src*="sticky_"] {
    width: 0;
    height: 0;
    padding-left: 19px;
    padding-top: 18px;
}

img[src*="topic_unread.gif"],
img[src*="topic_unread_hot.gif"],
img[src*="topic_unread_mine.gif"],
img[src*="topic_unread_hot_mine.gif"] {
    background-image: url(data:image/png;base64,);
}

img[src*="topic_read.gif"],
img[src*="topic_read_hot.gif"],
img[src*="topic_read_mine.gif"],
img[src*="topic_read_hot_mine.gif"] {
    background-image: url(data:image/png;base64,);
}

img[src*="sticky_unread.gif"] {
    background-image: url(data:image/png;base64,);
}

img[src*="sticky_read.gif"] {
    background-image: url(data:image/png;base64,);
}

img[src*="icon_topic_latest.gif"] {
    padding-left: 18px;
    padding-top: 9px;
    background-image: url(data:image/png;base64,);
}

* {
    color: #FFF;
}
}
