css:
@-moz-document domain(yurineko.net) {

  .bg-white {
    background-color: rgb(22 22 24);
    color: rgb(206 206 206);
  }

  .bg-gray-light {
    background-color: transparent;
  }

  .bg-pink .container .block img, .bg-pink .container a button i {
    filter: invert(1);
  }

  .bg-pink .container a button i:hover{
    filter: invert(0);
  }

  .bg-pink .container a:hover{
    color: #d9d9d9;
  }

  .bg-gray-200 {
     background-color: rgb(22 22 24)
  }

  .bg-gray-100 {
    background-image: linear-gradient(#4b4b4b2b, #1a1a1a33);
    background-color: transparent;
  }

  .bg-gray-700 {
    background-color: rgb(123 100 106);
  }

  .bg-blue-200 {
    background-color: rgb(28 35 43);
  }

  .bg-blue-300 {
    background-color: rgb(103 49 56);
  }

  .bg-tag {
    background-color:rgb(188 124 136);
  }

  .text-white {
    color:  rgb(32 35 44);
  }

  .text-gray-800 {
    color: rgb(206 206 206);
  }

  .text-gray-dark {
    color: rgb(117, 117, 117, .71);
  }

  .manga-list-box__name {
    background-color: transparent;
    color: #959595;
  } 
  .manga-list-box__group {
    background-color: transparent
  }

  .slick-list .bg-white {
    background-color: #0000007d;
  }

  .ant-divider {
    border-top: 1px solid rgb(255, 255, 255, .38);
  }

  .ant-input {
    background-color: #000000d4;
    color: rgb(206 206 206);
  }

  .ant-input-affix-wrapper {
    background-color: #000000;
    border: 0px;
  }

  .anticon.ant-input-clear-icon {
    color: rgb(172 172 172);
  }

  .anticon.ant-input-clear-icon:hover {
    color: rgb(172,172,172, 0.5) !important;
  }

 a {
    color: aliceblue;
  }

  h2 {
    color: rgb(206 206 206);
  }


  .text-gray-darkness {
    color: rgb(206 206 206);
  }

  .visited\:text-gray-300:visited {
    color: rgba(60,60,60);
  }

  .ant-pagination-disabled .ant-pagination-item-link {
    color: rgb(0, 0, 0, .9);
  }

  .hover\:bg-blue-light:hover {
    background-color: rgb(49 58 68);
  }

  .border-blue {
    border-color: aliceblue;
  }

  .text-black {
    color: rgb(152 152 152);
  }


  .manga-list-box__group p a span{
    color: rgb(167 167 167) !important;
  }

  .manga-list-box__group p a span:hover{
    color: rgba(243,128,143) !important;
  }

}
