css:
@-moz-document domain(www.wanikani.com){

/*--- REMOVE PANELS ---*/
  
.extra-study,
.row .recent-unlocks,
.row .low-percentage,
.row .recent-retired,
.global-header .navigation-shortcuts
  {
    display: none !important;
  }





/*--- SMALLER MAIN BUTTONS ---*/

.dashboard .progress-and-forecast{
    grid-template-rows: 120px auto auto;
}

.lessons-and-reviews{
	margin: 0;
}

.lessons-and-reviews ul{
    display: flex;
    justify-content: center;
    width: 100%;
    gap: 10px;
}

.lessons-and-reviews li{
    width: 100%;
    margin-bottom: 0px;
}

.lessons-and-reviews__button{
	background-position: 50% -10%;
    background-size: auto 85px;
    padding: 66px 16px 16px 16px;
}



/*--- CLEAN FOOTER ---*/

footer .container{
	padding-top: 80px;
    margin: 0;
}

footer .span12 li{
	font-size: 9px;
    width: 60%;
    text-align: center;
}

footer .span12 ul{
	display: flex;
}



/*--- COLORS ---*/
