.global-header{
	background: rgba(40, 40, 80, 0.9);
}

body .container #search__trigger__mobile{
	color: white;
}
body .container .navigation__toggle-lines{
	border-color: white;
}
body .container .navigation__toggle-lines::before{
	border-color: white;
}
body .container .navigation__toggle-lines::after{
	border-color: white;
}
*/

/* GOLD BURNED */

body .srs-progress #burned{
	background-image: linear-gradient(-45deg, #ffbf00, #b17c00);
}

  
  
