.level-up-alert .wk-button{
	color: white;
    text-shadow: none;
}

.level-up-alert{
    box-shadow: inset 0 -3px 1px rgba(0,0,0,0.2),inset 0 3px 1px rgba(0,0,0,0);
    border: 1px solid black;
    border-radius: 5px;
    background: #505565;
    padding: 10px;
}

*/
  



/*--- REMOVE PANELS ---*/

header .global-header .navigation-shortcuts{
    display: none !important;
}

.subject-legend,
.page-header__title-subtext{
    display: none;
}
    
.character-grid .progress-bar{
	display: none;
}



/*--- ITEM GRID ---*/

.character-item__badge{
	display: none;
}

.character-grid__item{
	border-radius: 20px;
}

.character-item--kanji .character-item--burned{
	background-color: #f0a;
	background-image: linear-gradient(to bottom, #f0a, #dd0093);
	border-color: #f6c rgba(0,0,0,0) #c08 #f6c;
}



/* rad and kan */

.character-grid__item--radical,
.character-grid__item--kanji{
 	flex: 0 0 25%;
    width: 100%;
}

.character-item--radical,
.character-item--kanji{
	width: 100%;
    flex-direction: column;
    box-shadow: 0 10px 10px rgba(0,0,0,0.4) inset;
}


.character-grid__item--radical .character-item__info-meaning,
.character-grid__item--kanji .character-item__info-meaning,
.character-grid__item--kanji .character-item__info-reading{
	text-align: center;
    padding-bottom: 4px;
    font-size: 10px;
    margin: 0 -10px 0 -10px;
}

.character-item__characters{
    padding: 10px;

}


/* voc grid */

.character-grid__item--vocabulary{
 	flex: 1 0 50%;
    width: 100%;
}

.character-item--vocabulary{
	width: 100%;
    flex-direction: column;
    box-shadow: 0 10px 10px rgba(0,0,0,0.4) inset;
}

.character-grid__item--vocabulary .character-item__info-meaning,
.character-grid__item--vocabulary .character-item__info-reading{
	text-align: center;
    padding-bottom: 4px;
}


/* grid colors */

.character-item--radical{
	background-image: linear-gradient(to bottom, #0af, #004d75);
    border-color: #88d7ff rgba(0,0,0,0) #069 #88d7ff;
    box-shadow: 0 10px 10px rgba(0,0,0,0.3) inset;
}

.character-item--radical.character-item--burned{
	background-image: linear-gradient(to bottom, #006fa6, #001f2f);
}



.character-item--kanji{
    background-image: linear-gradient(to bottom, #ff00a9, #660043);
    border-color: #f6c rgba(0,0,0,0) #c08 #f6c;
    box-shadow: 0 10px 10px rgba(0,0,0,0.3) inset;
}
.character-item--kanji.character-item--burned{
    background-image: linear-gradient(to bottom, #99005d, #13000a);
}



.character-item--vocabulary{
    background-image: linear-gradient(to bottom, #a0f, #5b0088);
    border-color: #c655ff rgba(0,0,0,0) #80c #c655ff;
    box-shadow: 0 10px 10px rgba(0,0,0,0.3) inset;
}

.character-item--vocabulary.character-item--burned{
    background-image: linear-gradient(to bottom, #7a00b7, #1f002f);
}


/* golden kanji characters */
.character-item--burned .character-item__characters{
	background-image: linear-gradient(to bottom, #ffa700, #bb663d);
    background-clip: text;
    -webkit-background-clip: text;
    -moz-background-clip: text;
    -webkit-text-fill-color: transparent;
    -moz-text-fill-color: transparent;
    -webkit-mask-image: linear-gradient(to bottom, #ffa700, #a44f26);
}



.component-character--radical.component-character--burned .component-character__characters{
    color: white;
	background-color: #0af;
    background-image: linear-gradient(to bottom, #0af, #0093dd);
    box-shadow: unset;
}







}
