css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("chrome://google-toolbar/content/new-tab.html") {

body {background: #000000 url(data:image/gif;base64,) repeat-x !important;}

#main {margin-top:5px !important;}

div[class="item-title"] div{text-decoration: none !important;color: #dae2f2 !important; text-shadow: #000000 1px 1px 2px !important;}
div[class="help-link"] a{text-decoration: none !important; color: #dae2f2 !important; text-shadow: #253960 1px 1px 1px !important;}

div#clicktoremove {background: transparent; color: #dd8e27 !important;}

div#undosaved-div {background: transparent; color: #253960 !important;}
div#undosaved-div a{color: #dd8e27 !important;}

div#mostvisited-div {display: none; color: transparent !important; position: absolute !important;}

img[id="gtbNtLogo"] {display: none !important;}

div#mostvisited {border:3px solid #dd8e27;background: #5a6986 !important;padding:3px 10px 3px 0px;-moz-border-radius:5px;margin-right:15px}
div#mostvisited div{padding:0px 0px 0px 9px;}
div#mostvisited td:hover{background: #354258;padding:3px 10px 3px 9px;-moz-border-radius:5px;}.thumbnail:hover{border:1px solid #dd8e27 !important;}

div#recentlyBookmarked {background: #6f82a5 !important;}
div#recentlyBookmarkedContainer {background: #dae2f2 !important; padding:3px 5px 2px 5px;}
div#recentlyBookmarked div{text-shadow: #ffffff 1px 1px 1px !important; color: #000000 !important;}
div#recentlyBookmarked div:hover{background: #ffffff;}

div[class="section-title"]{color: #253960 !important;}
span[class="section-title"]{color: #253960 !important;}

div#recentlyClosedTabs {background: #dd8e27 !important;}
div#recentlyClosedContainer {background: #ffdca9 !important; padding:3px 5px 2px 5px;}
div#recentlyClosedContainer div{text-shadow: #ffffff 1px 1px 1px !important; color: #6f82a5 !important;}
div#recentlyClosedContainer div:hover{background: #ffffff}

a[class="manage"] {
padding:0px 10px 0px 0px !important;
color: #dd8e27 !important;
text-shadow: #253960 1px 1px 1px !important;
font-weight:bold !important;
text-decoration: none !important;}

}
