css:
@-moz-document domain("www.youtube.com")
{
/* section de gauche */
  div#sections
  {
    background-color: #e5fff2;
    border-right: 2px solid rgba(100, 100, 100, 0.3);
  }

/* zone du haut */
  ytd-masthead[mode="legacy"] #container.ytd-masthead, ytd-masthead[mode="material-left"] #container.ytd-masthead
  {
    background-color: #d9b3ff;
    box-shadow: 0px 0px 20px 4px #111;
  }

/* zone principale */
  ytd-page-manager > *.ytd-page-manager
  {
    background-color: #fff5e6;
  }

/* section principale de la vidéo */
  #container.ytd-watch
  {
    box-shadow: 0px 0px 20px 4px #111;
    padding: 10px;
    border-radius: 15px;
    background: #ffe5e5;
  }

  img.yt-img-shadow
  {
    border-radius: 5px;
  }

  #container.ytd-sentiment-bar-renderer
  {
    height: 5px;
    background-color: hsla(0, 100%, 50%, 0.4);
  }

  #like-bar.ytd-sentiment-bar-renderer
  {
    height: 5px;
  }

  paper-button[subscribed].ytd-subscribe-button-renderer
  {
    background-color: #b3e6b3;
  }

  #owner-name.ytd-video-owner-renderer
  {
    font-size: 2rem;
    line-height: 2rem;
  }

  .super-title.ytd-video-primary-info-renderer
  {
    line-height: 1.5rem;
    background-color: gold;
  }

/* titres des vidéos */
  .title.ytd-video-primary-info-renderer
  {
    text-align: center;
    color: black;
    text-shadow: #fff5e6 0.08em 0em 0em;
    padding-bottom: 20px;
    font-weight: 580;
    font-size: 20px;
  }

/* pouce bleu */
  ytd-toggle-button-renderer.style-default-active[is-icon-button]
  {
    color: #7ca6ef;
  }

/* couleur de la barre de progression des vidéos */
  .ytp-play-progress, .ytp-swatch-background-color
  {
    background-color: #d9b3ff;
    background: rgb(51,51,51);
    background: -moz-linear-gradient(left, rgba(51,51,51,1) 0%, rgba(51,51,51,1) 52%, rgba(229,133,43,1) 100%);
    background: -webkit-linear-gradient(left, rgba(51,51,51,1) 0%,rgba(51,51,51,1) 52%,rgba(229,133,43,1) 100%);
    background: linear-gradient(to right, rgba(51,51,51,1) 0%,rgba(51,51,51,1) 52%,rgba(229,133,43,1) 100%);
  }


/* bordure de la video */
  .html5-video-player:not(.ytp-transparent), .html5-video-player.ad-interrupting, .html5-video-player.ended-mode
  {
    background-color: #000;
    border-radius: 15px 0px 15px 0px;
  }

  ytd-watch:not([transparent-player-background_]) #player-container.ytd-watch, ytd-watch[flexy-fit-to-video_]:not([is-currently-flexible_]) #player-container.ytd-watch, ytd-watch[theater] #player-container.ytd-watch
  {
    border-radius: 15px;
  }

/* bordure image du youtuber */
  #avatar.ytd-video-owner-renderer
  {
    border-radius: 0%;
  }

/* enlève la vignette du youuber en bas à droite de la vidéo */
  .iv-branding .branding-img-container img
  {
    opacity: 0;
  }

/* barre de progression */
  .ytp-progress-list
  {
    background: rgba(255,255,255,0);
  }

  .ytp-load-progress
  {
    opacity: 0.5;
    border-color: white;
  }
/* boule */
  .ytp-scrubber-container
  {
    display: none;
  }

/* titre aujour'hui hier ... */
#title.ytd-shelf-renderer {
    color: rgba(229,133,43,1);
    font-size: 2rem;
}

