css:
@-moz-document url-prefix('https://twitter.com') {

    :root {
        --main-color: #e2ff63;
        --second-color: #BBD352;
        --bg-url: url('https://pbs.twimg.com/media/Fm6chi0XgAIesi1?format=jpg&name=4096x4096');
    /*
        Yellow:
            primary: #e2ff63
            secondshadow: #BBD352
            bg: https://pbs.twimg.com/media/Fm6chi0XgAIesi1?format=jpg&name=4096x4096
        Red:
            primary: #ff005b
            secondshadow: #de004e
            bg: https://pbs.twimg.com/media/D8Y7BFvVsAAEtAe?format=jpg&name=medium
        var(--main-color)
        var(--second-color)
        var(--bg-url)
    */
    }

    body {
        height: 100% !important;
        background-color: rgb(0, 0, 0, 0.65) !important;
        background-image: var(--bg-url) !important;
        background-size: cover;
        background-repeat: no-repeat;
        background-attachment: fixed;
    }

    [aria-label="Twitter Blue"],
    [aria-label="Verified Organizations"],
    [aria-label="Verified"],
    [aria-label="Terms of Service"],
    [aria-label="Get Verified"] {
        display: none;
    }



    .message,
    .DashboardProfileCard.module,
    .typeahead,
    .follow-card .follow-bar,
    #dm_dialog_new,
    .tweet-box.rich-editor.notie,
    input,
    button,
    select,
    textarea,
    .modal-footer {
        border: rgb(0, 0, 0, 0.65) !important;
    }

    /* ===== Scrollbar CSS ===== */
    /* Firefox */
    * {
        scrollbar-width: auto;
        scrollbar-color: var(--main-color) #ffffff;
    }

    /* Chrome, Edge, and Safari */
    *::-webkit-scrollbar {
        width: 10px;
        height: 10px;
    }

    ::-webkit-scrollbar-track-piece:start {
        background: transparent repeat-y !important;
    }

    ::-webkit-scrollbar-track-piece:end {
        background: transparent repeat-y !important;
    }

    *::-webkit-scrollbar-track {
        background-color: rgb(0, 0, 0, 0.65) !important;
    }

    *::-webkit-scrollbar-thumb {
        background-color: var(--main-color);
        border-radius: 10px;
    }



    /*  */

    body::-webkit-scrollbar {
        width: 9px;
        height: 9px;
    }

    body::-webkit-scrollbar-button:start:decrement,
    body::-webkit-scrollbar-button:end:increment {
        display: block;
        height: 0;
        background-color: transparent;
    }

    body::-webkit-scrollbar-track-piece {
        background-color: #ffffff;
        opacity: 0.2;

        /* Here */
        display: none;

        -webkit-border-radius: 0;
        -webkit-border-bottom-right-radius: 14px;
        -webkit-border-bottom-left-radius: 14px;
    }

    body::-webkit-scrollbar-thumb:vertical {
        height: 50px;
        background-color: var(--main-color);
        -webkit-border-radius: 8px;
    }


    .r-urgr8i {
        background-color: rgb(0, 0, 0, 0.65);
    }

    .r-1q3imqu {
        background-color: rgb(0, 0, 0, 0.65);
    }


    .r-yfoy6g {
