        border: 1px solid;
    }


    .icon {
        background-image: url(https://dl.dropboxusercontent.com/u/28270774/Stylish/Twitter/twitter_web_sprite_icons.png);
    }

    .account-summary .account-group,
    .account-summary .account-action {
        padding-top: 5px !important;
    }

    .Icon--cogWithCaret {
        padding-right: 5px !important;
    }

    .dm-dialog .twttr-dialog-footer,
    .rosetta .dm-tweetbox {
        padding-bottom: 9px !important;
    }

    .go-to-profile,
    .dm-dialog .twttr-dialog-footer,
    #global-tweet-dialog .modal-tweet-form-container {
        border-bottom: 1px solid #000 !important;
        border-radius: 0 0 5px 5px !important;
    }

    .profile-summary .account-summary,
    body.user-style-xIGBClutchIx .wrapper-profile .profile-card.profile-header .profile-header-inner,
    .wrapper-profile .profile-card.profile-header .profile-header-inner,
    .more-tweet-actions .dropdown .dropdown-menu,
    .session .dropdown-menu,
    .global-nav .pull-right .dropdown-menu,
    .global-nav .secondary-nav .dropdown-menu,
    .dropdown.open .dropdown-menu,
    .modal-header {
        border-top: 1px solid #000 !important;
        border-radius: 5px 5px 0 0 !important;
    }


    a:link {
        color: var(--main-color) !important;
    }

    pre,
    a:visited {
        color: var(--main-color) !important;
    }

    h1,
    h1 *,
    h2,
    h2 *,
    h3,
    h3 *,
    h4,
    h4 *,
    h5,
    h5 *,
    h6,
    h6 *,
    a:hover {
        color: var(--main-color) !important;
    }

    b,
    b *,
    strong,
    strong * {
        color: var(--main-color) !important;
    }

    li,
    i,
    i * {
        color: var(--main-color) !important;
    }

    .profile-header-inner-overlay,
    .caret,
    .inline-reply-caret,
    .dm-caret,
    .search-header .btn-group .dropdown-caret,
    .dropdown-menu .dropdown-caret,
    .icon.nav-search {
        display: none !important;
    }


    .tweet-btn {
        color: #000 !important
    }

    .dm-dialog .twttr-dialog-header {
        border: 0 !important;
        border-bottom: 1px solid #000 !important;
        border-left: 1px solid #000 !important;
        border-right: 1px solid #000 !important;
        border-top: 1px solid #000 !important;
    }

    .modal-footer {
        border-radius: 0 0 5px 5px !important;
    }

    .UserActions .user-actions .dropdown-menu,
    .more-tweet-actions .dropdown .dropdown-menu,
    .dropdown.open .dropdown-menu {
        border-radius: 5px 5px 5px 5px !important;
    }


    .front-page-photo-set .front-welcome-text p,
    .front-page-photo-set .front-welcome-text h1 {
        color: #000 !important;
    }

    .ProfileTweet.is-favorited .ProfileTweet-action--favorite .ProfileTweet-actionButtonUndo *,
    .tweet.favorited .tweet-actions .Icon--favorite,
    .tweet.favorited .ProfileTweet-actionList .Icon--favorite {
        color: #00ffff !important;
    }

    .ProfileTweet-actionButtonUndo * {
        color: #00ffff !important;
    }


